  const [nextPrayer, setNextPrayer] = useState(null);
  const [countdown, setCountdown] = useState('');
  const [greeting, setGreeting] = useState('');

  const level = getLevel();
  const levelProgress = level.nextXp ? ((xp - (level.nextXp - 500)) / 500) * 100 : 100;

  // Calculate progress
  const prayersDone = Object.values(prayers).filter(p => p === 'on_time' || p === 'late').length;
  const prayerProgress = (prayersDone / 5) * 100;
  const habitsDone = habits.filter(h => h.completed).length;
  const habitsProgress = habits.length > 0 ? (habitsDone / habits.length) * 100 : 0;

  // Get time-based greeting
  useEffect(() => {
    const hour = new Date().getHours();
    if (hour < 12) setGreeting('Good Morning');
    else if (hour < 17) setGreeting('Good Afternoon');
    else if (hour < 21) setGreeting('Good Evening');
    else setGreeting('Good Night');
  }, []);

  useEffect(() => {
    axios.get('http://localhost:5000/api/daily-ayah')
      .then(res => setDailyAyah(res.data))
      .catch(console.error);
  }, []);

  useEffect(() => {
    if (!location) return;

    axios.get(`http://localhost:5000/api/prayer-times?latitude=${location.latitude}&longitude=${location.longitude}`)
      .then(res => {
        const data = res.data;
        const now = new Date();
        const prayerOrder = ['fajr', 'sunrise', 'dhuhr', 'asr', 'maghrib', 'isha'];

        for (const name of prayerOrder) {
          if (name === 'sunrise') continue;
          const time = new Date(data[name]);
          if (time > now) {
            setNextPrayer({ name, time });
            break;
          }
        }
      })
      .catch(console.error);
  }, [location]);

  useEffect(() => {
    if (!nextPrayer) return;

    const interval = setInterval(() => {
      const now = new Date();
      const diff = nextPrayer.time - now;

      if (diff <= 0) {
        setCountdown('Now');
        return;
      }

      const hours = Math.floor(diff / (1000 * 60 * 60));
      const minutes = Math.floor((diff % (1000 * 60 * 60)) / (1000 * 60));
      const seconds = Math.floor((diff % (1000 * 60)) / 1000);

      if (hours > 0) {
        setCountdown(`${hours}h ${minutes}m`);
      } else {
        setCountdown(`${minutes}m ${seconds}s`);
      }
    }, 1000);

    return () => clearInterval(interval);
  }, [nextPrayer]);

