import React, { useState, useEffect } from 'react';
import { useAuth, TIERS } from '../context/AuthContext';
import { doc, getDoc, setDoc } from 'firebase/firestore';
import { db } from '../config/firebase';
import { 
  Dumbbell, Check, ChevronDown, ChevronUp, Target, 
  Plus, Minus, Scale, TrendingDown, Upload, FileText, Trash2,
  ToggleLeft, ToggleRight, Lock, Crown, X, Edit2
} from 'lucide-react';
import FeatureGate from '../components/FeatureGate';

function Workout() {
  const { user, hasAccess, getTier } = useAuth();
  const tier = getTier();

  const [workoutEnabled, setWorkoutEnabled] = useState(false);
  const [customPdf, setCustomPdf] = useState(null);
  const [loading, setLoading] = useState(true);

  // Custom workout plan (user creates their own)
  const [customPlan, setCustomPlan] = useState([]);
  const [addingDay, setAddingDay] = useState(false);
  const [newDayName, setNewDayName] = useState('');
  const [editingDay, setEditingDay] = useState(null);
  const [newExercise, setNewExercise] = useState({ name: '', sets: 3, reps: '10' });

  const [weightInput, setWeightInput] = useState('');
  const [showWeightInput, setShowWeightInput] = useState(false);
  const [weightHistory, setWeightHistory] = useState([]);
  const [currentWeight, setCurrentWeight] = useState(null);

  const [exerciseData, setExerciseData] = useState({});
  const [completedWorkouts, setCompletedWorkouts] = useState({});
  const [expandedDay, setExpandedDay] = useState(null);

  // Load user workout data
  useEffect(() => {
    if (user) {
      loadWorkoutData();
    }
  }, [user]);

  const loadWorkoutData = async () => {
    setLoading(true);
    try {
      const docRef = doc(db, 'users', user.uid, 'fitness', 'workout');
      const docSnap = await getDoc(docRef);

      if (docSnap.exists()) {
        const data = docSnap.data();
        setWorkoutEnabled(data.enabled || false);
        setCustomPdf(data.customPdf || null);
        setCustomPlan(data.customPlan || []);
        setWeightHistory(data.weightHistory || []);
        setCurrentWeight(data.currentWeight || null);
        setExerciseData(data.exerciseData || {});
        setCompletedWorkouts(data.completedWorkouts || {});
      }
    } catch (error) {
      console.error('Error loading workout data:', error);
    }
    setLoading(false);
  };

  const saveWorkoutData = async (updates) => {
    if (!user) return;
    try {
      const docRef = doc(db, 'users', user.uid, 'fitness', 'workout');
      await setDoc(docRef, {
        enabled: workoutEnabled,
        customPdf,
        customPlan,
        weightHistory,
        currentWeight,
        exerciseData,
        completedWorkouts,
        ...updates,
        updatedAt: new Date().toISOString()
      }, { merge: true });
    } catch (error) {
      console.error('Error saving workout data:', error);
    }
  };

  const toggleWorkout = async () => {
    const newValue = !workoutEnabled;
    setWorkoutEnabled(newValue);
    await saveWorkoutData({ enabled: newValue });
  };

  const handlePdfUpload = async (e) => {
    const file = e.target.files[0];
    if (!file) return;

    if (file.type !== 'application/pdf') {
      alert('Please upload a PDF file');
      return;
    }

    if (file.size > 5 * 1024 * 1024) {
      alert('File size must be less than 5MB');
      return;
    }

    const pdfData = {
      name: file.name,
      size: file.size,
      uploadedAt: new Date().toISOString()
    };
    setCustomPdf(pdfData);
    await saveWorkoutData({ customPdf: pdfData });
    alert('Workout PDF uploaded! You can now create your workout plan below.');
  };

  const handleRemovePdf = async () => {
    setCustomPdf(null);
    await saveWorkoutData({ customPdf: null });
  };

  // Add a new workout day
  const addWorkoutDay = async () => {
    if (!newDayName.trim()) return;

    const newDay = {
      id: `day_${Date.now()}`,
      name: newDayName.trim(),
      exercises: []
    };

    const updatedPlan = [...customPlan, newDay];
    setCustomPlan(updatedPlan);
    setNewDayName('');
    setAddingDay(false);
    await saveWorkoutData({ customPlan: updatedPlan });
  };

  // Delete a workout day
  const deleteWorkoutDay = async (dayId) => {
    if (!window.confirm('Delete this workout day?')) return;

    const updatedPlan = customPlan.filter(d => d.id !== dayId);
    setCustomPlan(updatedPlan);
    await saveWorkoutData({ customPlan: updatedPlan });
  };

  // Add exercise to a day
  const addExerciseToDay = async (dayId) => {
    if (!newExercise.name.trim()) return;

    const exercise = {
      id: `ex_${Date.now()}`,
      name: newExercise.name.trim(),
      targetSets: parseInt(newExercise.sets),
      targetReps: newExercise.reps
    };

    const updatedPlan = customPlan.map(day => {
      if (day.id === dayId) {
        return { ...day, exercises: [...day.exercises, exercise] };
      }
      return day;
    });

    setCustomPlan(updatedPlan);
    setNewExercise({ name: '', sets: 3, reps: '10' });
    await saveWorkoutData({ customPlan: updatedPlan });
  };

  // Delete exercise from a day
  const deleteExercise = async (dayId, exerciseId) => {
    const updatedPlan = customPlan.map(day => {
      if (day.id === dayId) {
        return { ...day, exercises: day.exercises.filter(e => e.id !== exerciseId) };
      }
      return day;
    });

    setCustomPlan(updatedPlan);
    await saveWorkoutData({ customPlan: updatedPlan });
  };

  const handleWeightLog = async () => {
    if (!weightInput) return;

    const weight = parseFloat(weightInput);
    const newHistory = [...weightHistory, { weight, date: new Date().toISOString() }];

    setWeightHistory(newHistory);
    setCurrentWeight(weight);
    setWeightInput('');
    setShowWeightInput(false);

    await saveWorkoutData({ 
      weightHistory: newHistory, 
      currentWeight: weight 
    });
  };

  const getExerciseData = (exerciseId) => {
    return exerciseData[exerciseId] || { completed: false, sets: [] };
  };

  const toggleExercise = async (exerciseId, targetSets) => {
    const current = exerciseData[exerciseId] || { completed: false, sets: [] };
    const newData = {
      ...exerciseData,
      [exerciseId]: {
        completed: !current.completed,
        sets: current.completed ? [] : Array(targetSets).fill({ reps: '', weight: '' })
      }
    };
    setExerciseData(newData);
    await saveWorkoutData({ exerciseData: newData });
  };

  const updateSet = async (exerciseId, setIndex, field, value) => {
    const current = exerciseData[exerciseId] || { completed: true, sets: [] };
    const newSets = [...current.sets];
    newSets[setIndex] = { ...newSets[setIndex], [field]: value };

    const newData = {
      ...exerciseData,
      [exerciseId]: { ...current, sets: newSets }
    };
    setExerciseData(newData);
  };

  const isDayComplete = (day) => {
    return day.exercises.every(ex => getExerciseData(ex.id).completed);
  };

  const markDayComplete = async (dayId) => {
    if (!completedWorkouts[dayId]) {
      const newCompleted = { ...completedWorkouts, [dayId]: new Date().toISOString() };
      setCompletedWorkouts(newCompleted);
      await saveWorkoutData({ completedWorkouts: newCompleted });
    }
  };

  const startWeight = weightHistory[0]?.weight;
  const weightLost = startWeight && currentWeight ? (startWeight - currentWeight).toFixed(1) : null;

  // Check if user has access to workout
  if (!hasAccess('workout')) {
    return (
      <div className="workout-page">
        <header className="page-header">
          <h1> Workout</h1>
          <p className="subtitle">Track your fitness journey</p>
        </header>
        <FeatureGate feature="workout">
          <div style={{ minHeight: 400 }} />
        </FeatureGate>
      </div>
    );
  }

  if (loading) {
    return (
      <div className="workout-page">
        <div className="loading-state">
          <div className="spinner" />
          <p>Loading...</p>
        </div>
      </div>
    );
  }

  // Workout not enabled - show enable screen
  if (!workoutEnabled) {
    return (
      <div className="workout-page">
        <header className="page-header">
          <h1> Workout</h1>
          <p className="subtitle">Track your fitness journey</p>
        </header>

        <div className="enable-card">
          <Dumbbell size={48} className="enable-icon" />
          <h2>Enable Workout Tracking?</h2>
          <p>Import your workout PDF and create your personalized training plan.</p>

          <button className="btn btn-primary enable-btn" onClick={toggleWorkout}>
            <ToggleRight size={20} />
            Enable Workout
          </button>
        </div>

        <style>{`
          .workout-page {
            padding: 20px;
            padding-bottom: 100px;
          }
          .page-header h1 { font-size: 28px; margin-bottom: 4px; }
          .subtitle { color: var(--text-muted); font-size: 14px; }
          .enable-card {
            background: var(--bg-surface);
            border-radius: 20px;
            padding: 40px;
            text-align: center;
            margin-top: 40px;
          }
          .enable-icon {
            color: var(--gold);
            margin-bottom: 20px;
          }
          .enable-card h2 {
            font-size: 22px;
            margin-bottom: 12px;
          }
          .enable-card p {
            color: var(--text-muted);
            font-size: 14px;
            margin-bottom: 24px;
            max-width: 300px;
            margin-left: auto;
            margin-right: auto;
          }
          .enable-btn {
            padding: 16px 32px;
            font-size: 16px;
          }
          .btn {
            display: inline-flex;
            align-items: center;
            justify-content: center;
            gap: 8px;
            border: none;
            border-radius: 12px;
            font-weight: 600;
            cursor: pointer;
          }
          .btn-primary {
            background: var(--gold);
            color: var(--bg-primary);
          }
          .loading-state {
            display: flex;
            flex-direction: column;
            align-items: center;
            justify-content: center;
            min-height: 300px;
          }
          .spinner {
            width: 40px;
            height: 40px;
            border: 3px solid var(--bg-surface-light);
            border-top-color: var(--gold);
            border-radius: 50%;
            animation: spin 1s linear infinite;
            margin-bottom: 16px;
          }
          @keyframes spin { to { transform: rotate(360deg); } }
        `}</style>
      </div>
    );
  }

  return (
    <div className="workout-page">
      <header className="page-header">
        <div className="header-content">
          <h1> Workout</h1>
          <p className="subtitle">Your Custom Training Plan</p>
        </div>
        <button className="disable-btn" onClick={toggleWorkout} title="Disable Workout">
          <ToggleLeft size={18} />
        </button>
      </header>

      {/* PDF Upload Section */}
      <div className="pdf-section">
        <div className="pdf-header">
          <FileText size={18} />
          <span>Your Workout Plan PDF</span>
        </div>

        {customPdf ? (
          <div className="pdf-uploaded">
            <div className="pdf-info">
              <span className="pdf-name">{customPdf.name}</span>
              <span className="pdf-date">Uploaded {new Date(customPdf.uploadedAt).toLocaleDateString()}</span>
            </div>
            <button className="remove-pdf-btn" onClick={handleRemovePdf}>
              <Trash2 size={16} />
            </button>
          </div>
        ) : (
          <label className="upload-pdf-btn">
            <Upload size={16} />
            <span>Upload Your Workout PDF</span>
            <input type="file" accept=".pdf" onChange={handlePdfUpload} hidden />
          </label>
        )}

        <p className="pdf-hint">Upload your workout plan PDF, then create your exercises below</p>
      </div>

      {/* Weight Tracker - Pro+ Only */}
      {tier === TIERS.PRO_PLUS ? (
        <div className="weight-tracker">
          <div className="weight-header">
            <Scale size={18} />
            <h3>Weight Tracker</h3>
            <button 
              className="log-weight-btn"
              onClick={() => setShowWeightInput(!showWeightInput)}
            >
              {showWeightInput ? 'Cancel' : '+ Log'}
            </button>
          </div>

          {showWeightInput && (
            <div className="weight-input-row">
              <input
                type="number"
                placeholder="Weight"
                value={weightInput}
                onChange={(e) => setWeightInput(e.target.value)}
                step="0.1"
              />
              <span>lbs</span>
              <button className="save-weight-btn" onClick={handleWeightLog}>Save</button>
            </div>
          )}

          <div className="weight-stats">
            <div className="weight-stat">
              <span className="weight-value">{currentWeight || '--'}</span>
              <span className="weight-label">Current</span>
            </div>
            <div className="weight-stat">
              <span className="weight-value">{startWeight || '--'}</span>
              <span className="weight-label">Start</span>
            </div>
            <div className="weight-stat">
              <span className="weight-value">{weightLost && weightLost > 0 ? `-${weightLost}` : '--'}</span>
              <span className="weight-label">Lost</span>
            </div>
          </div>
        </div>
      ) : (
        <div className="weight-locked">
          <Lock size={16} />
          <span>Weight tracking available in Pro+</span>
          <a href="/pricing">Upgrade</a>
        </div>
      )}

      {/* Custom Workout Plan */}
      <div className="workout-plan">
        <div className="plan-header">
          <h3>Your Workout Plan</h3>
          <button className="add-day-btn" onClick={() => setAddingDay(true)}>
            <Plus size={16} /> Add Day
          </button>
        </div>

        {addingDay && (
          <div className="add-day-form">
            <input
              type="text"
              placeholder="Day name (e.g., Push Day, Leg Day)"
              value={newDayName}
              onChange={(e) => setNewDayName(e.target.value)}
              autoFocus
            />
            <div className="add-day-actions">
              <button className="btn btn-secondary" onClick={() => { setAddingDay(false); setNewDayName(''); }}>
                Cancel
              </button>
              <button className="btn btn-primary" onClick={addWorkoutDay}>
                Add Day
              </button>
            </div>
          </div>
        )}

        {customPlan.length === 0 ? (
          <div className="empty-plan">
            <Dumbbell size={40} />
            <h4>No Workout Days Yet</h4>
            <p>Upload your PDF and add workout days to get started</p>
          </div>
        ) : (
          <div className="days-list">
            {customPlan.map((day) => {
              const isExpanded = expandedDay === day.id;
              const dayComplete = isDayComplete(day);
              const completedCount = day.exercises.filter(ex => getExerciseData(ex.id).completed).length;

              return (
                <div key={day.id} className={`day-card ${dayComplete ? 'completed' : ''}`}>
                  <div className="day-header" onClick={() => setExpandedDay(isExpanded ? null : day.id)}>
                    <div className="day-info">
                      <h4>{day.name}</h4>
                      <span className="exercise-count">
                        {completedCount}/{day.exercises.length} exercises
                      </span>
                    </div>
                    <div className="day-actions">
                      {dayComplete && <Check size={18} className="complete-icon" />}
                      {isExpanded ? <ChevronUp size={18} /> : <ChevronDown size={18} />}
                    </div>
                  </div>

                  {isExpanded && (
                    <div className="day-content">
                      {day.exercises.length === 0 ? (
                        <p className="no-exercises">No exercises added yet</p>
                      ) : (
                        <div className="exercises-list">
                          {day.exercises.map((exercise) => {
                            const data = getExerciseData(exercise.id);
                            return (
                              <div key={exercise.id} className={`exercise-item ${data.completed ? 'done' : ''}`}>
                                <div className="exercise-header" onClick={() => toggleExercise(exercise.id, exercise.targetSets)}>
                                  <div className={`exercise-check ${data.completed ? 'checked' : ''}`}>
                                    {data.completed && <Check size={14} />}
                                  </div>
                                  <div className="exercise-info">
                                    <span className="exercise-name">{exercise.name}</span>
                                    <span className="exercise-target">{exercise.targetSets}  {exercise.targetReps}</span>
                                  </div>
                                  <button 
                                    className="delete-exercise"
                                    onClick={(e) => { e.stopPropagation(); deleteExercise(day.id, exercise.id); }}
                                  >
                                    <X size={14} />
                                  </button>
                                </div>

                                {data.completed && (
                                  <div className="sets-tracker">
                                    {data.sets.map((set, i) => (
                                      <div key={i} className="set-row">
                                        <span className="set-num">Set {i + 1}</span>
                                        <div className="set-inputs">
                                          <input
                                            type="number"
                                            placeholder="Reps"
                                            value={set.reps}
                                            onChange={(e) => updateSet(exercise.id, i, 'reps', e.target.value)}
                                          />
                                          <input
                                            type="number"
                                            placeholder="lbs"
                                            value={set.weight}
                                            onChange={(e) => updateSet(exercise.id, i, 'weight', e.target.value)}
                                          />
                                        </div>
                                      </div>
                                    ))}
                                  </div>
                                )}
                              </div>
                            );
                          })}
                        </div>
                      )}

                      {/* Add Exercise Form */}
                      {editingDay === day.id ? (
                        <div className="add-exercise-form">
                          <input
                            type="text"
                            placeholder="Exercise name"
                            value={newExercise.name}
                            onChange={(e) => setNewExercise(p => ({ ...p, name: e.target.value }))}
                          />
                          <div className="exercise-params">
                            <input
                              type="number"
                              placeholder="Sets"
                              value={newExercise.sets}
                              onChange={(e) => setNewExercise(p => ({ ...p, sets: e.target.value }))}
                            />
                            <input
                              type="text"
                              placeholder="Reps"
                              value={newExercise.reps}
                              onChange={(e) => setNewExercise(p => ({ ...p, reps: e.target.value }))}
                            />
                          </div>
                          <div className="add-exercise-actions">
                            <button className="btn btn-secondary" onClick={() => setEditingDay(null)}>Cancel</button>
                            <button className="btn btn-primary" onClick={() => { addExerciseToDay(day.id); setEditingDay(null); }}>Add</button>
                          </div>
                        </div>
                      ) : (
                        <button className="add-exercise-btn" onClick={() => setEditingDay(day.id)}>
                          <Plus size={14} /> Add Exercise
                        </button>
                      )}

                      <div className="day-footer">
                        {dayComplete && !completedWorkouts[day.id] && (
                          <button className="complete-day-btn" onClick={() => markDayComplete(day.id)}>
                            <Check size={16} /> Mark Day Complete
                          </button>
                        )}
                        <button className="delete-day-btn" onClick={() => deleteWorkoutDay(day.id)}>
                          <Trash2 size={14} /> Delete Day
                        </button>
                      </div>
                    </div>
                  )}
                </div>
              );
            })}
          </div>
        )}
      </div>

      <style>{`
        .workout-page {
          padding: 20px;
          padding-bottom: 100px;
        }
        .page-header {
          display: flex;
          justify-content: space-between;
          align-items: flex-start;
          margin-bottom: 20px;
        }
        .page-header h1 { font-size: 28px; margin-bottom: 4px; }
        .subtitle { color: var(--gold); font-size: 13px; font-weight: 500; }
        .disable-btn {
          background: var(--bg-surface);
          border: none;
          width: 36px;
          height: 36px;
          border-radius: 10px;
          color: var(--text-muted);
          cursor: pointer;
          display: flex;
          align-items: center;
          justify-content: center;
        }

        .pdf-section {
          background: var(--bg-surface);
          border-radius: 14px;
          padding: 16px;
          margin-bottom: 16px;
        }
        .pdf-header {
          display: flex;
          align-items: center;
          gap: 10px;
          margin-bottom: 12px;
          font-size: 14px;
          font-weight: 500;
        }
        .pdf-uploaded {
          display: flex;
          align-items: center;
          justify-content: space-between;
          padding: 12px;
          background: var(--bg-surface-light);
          border-radius: 10px;
        }
        .pdf-name { font-size: 13px; display: block; }
        .pdf-date { font-size: 11px; color: var(--text-muted); }
        .remove-pdf-btn {
          background: rgba(231,76,60,0.2);
          border: none;
          width: 32px;
          height: 32px;
          border-radius: 8px;
          color: var(--error);
          cursor: pointer;
          display: flex;
          align-items: center;
          justify-content: center;
        }
        .upload-pdf-btn {
          display: flex;
          align-items: center;
          justify-content: center;
          gap: 8px;
          width: 100%;
          padding: 14px;
          background: var(--bg-surface-light);
          border: 2px dashed var(--text-muted);
          border-radius: 10px;
          color: var(--text-secondary);
          font-size: 13px;
          cursor: pointer;
        }
        .upload-pdf-btn:hover {
          border-color: var(--gold);
          color: var(--gold);
        }
        .pdf-hint {
          font-size: 11px;
          color: var(--text-muted);
          margin-top: 10px;
          text-align: center;
        }

        .weight-tracker {
          background: var(--bg-surface);
          border-radius: 14px;
          padding: 16px;
          margin-bottom: 16px;
        }
        .weight-header {
          display: flex;
          align-items: center;
          gap: 10px;
          margin-bottom: 14px;
        }
        .weight-header h3 { flex: 1; font-size: 15px; }
        .log-weight-btn {
          background: var(--primary);
          border: none;
          padding: 8px 14px;
          border-radius: 8px;
          color: var(--gold);
          font-size: 12px;
          font-weight: 600;
          cursor: pointer;
        }
        .weight-input-row {
          display: flex;
          align-items: center;
          gap: 10px;
          margin-bottom: 14px;
        }
        .weight-input-row input {
          flex: 1;
          padding: 12px;
          background: var(--bg-surface-light);
          border: none;
          border-radius: 8px;
          color: var(--text-primary);
          font-size: 16px;
        }
        .save-weight-btn {
          background: var(--gold);
          border: none;
          padding: 12px 20px;
          border-radius: 8px;
          color: var(--bg-primary);
          font-weight: 600;
          cursor: pointer;
        }
        .weight-stats {
          display: grid;
          grid-template-columns: repeat(3, 1fr);
          gap: 10px;
        }
        .weight-stat {
          text-align: center;
          padding: 12px;
          background: var(--bg-surface-light);
          border-radius: 10px;
        }
        .weight-value {
          display: block;
          font-size: 22px;
          font-weight: 700;
          color: var(--gold);
        }
        .weight-label { font-size: 11px; color: var(--text-muted); }
        .weight-locked {
          display: flex;
          align-items: center;
          gap: 8px;
          padding: 14px;
          background: var(--bg-surface);
          border-radius: 12px;
          margin-bottom: 16px;
          font-size: 13px;
          color: var(--text-muted);
        }
        .weight-locked a {
          margin-left: auto;
          color: var(--gold);
          text-decoration: none;
          font-weight: 600;
        }

        .workout-plan {
          background: var(--bg-surface);
          border-radius: 16px;
          padding: 20px;
        }
        .plan-header {
          display: flex;
          justify-content: space-between;
          align-items: center;
          margin-bottom: 16px;
        }
        .plan-header h3 { font-size: 16px; }
        .add-day-btn {
          display: flex;
          align-items: center;
          gap: 6px;
          background: var(--primary);
          border: none;
          padding: 10px 16px;
          border-radius: 10px;
          color: var(--gold);
          font-size: 13px;
          font-weight: 600;
          cursor: pointer;
        }
        .add-day-form {
          background: var(--bg-surface-light);
          padding: 16px;
          border-radius: 12px;
          margin-bottom: 16px;
        }
        .add-day-form input {
          width: 100%;
          padding: 12px;
          background: var(--bg-primary);
          border: none;
          border-radius: 8px;
          color: var(--text-primary);
          font-size: 14px;
          margin-bottom: 12px;
        }
        .add-day-actions {
          display: flex;
          gap: 10px;
          justify-content: flex-end;
        }
        .btn {
          display: inline-flex;
          align-items: center;
          justify-content: center;
          gap: 6px;
          padding: 10px 16px;
          border: none;
          border-radius: 8px;
          font-size: 13px;
          font-weight: 600;
          cursor: pointer;
        }
        .btn-primary {
          background: var(--gold);
          color: var(--bg-primary);
        }
        .btn-secondary {
          background: var(--bg-surface);
          color: var(--text-secondary);
        }

        .empty-plan {
          text-align: center;
          padding: 40px 20px;
        }
        .empty-plan svg { color: var(--text-muted); margin-bottom: 16px; }
        .empty-plan h4 { margin-bottom: 8px; }
        .empty-plan p { color: var(--text-muted); font-size: 13px; }

        .days-list {
          display: flex;
          flex-direction: column;
          gap: 12px;
        }
        .day-card {
          background: var(--bg-surface-light);
          border-radius: 14px;
          overflow: hidden;
          border-left: 4px solid var(--text-muted);
        }
        .day-card.completed { border-left-color: var(--success); }
        .day-header {
          display: flex;
          justify-content: space-between;
          align-items: center;
          padding: 16px;
          cursor: pointer;
        }
        .day-info h4 { font-size: 15px; margin-bottom: 2px; }
        .exercise-count { font-size: 12px; color: var(--text-muted); }
        .day-actions {
          display: flex;
          align-items: center;
          gap: 8px;
          color: var(--text-muted);
        }
        .complete-icon { color: var(--success); }
        .day-content {
          padding: 0 16px 16px;
          border-top: 1px solid var(--bg-primary);
        }
        .no-exercises {
          text-align: center;
          padding: 20px;
          color: var(--text-muted);
          font-size: 13px;
        }
        .exercises-list {
          display: flex;
          flex-direction: column;
          gap: 8px;
          margin-bottom: 12px;
        }
        .exercise-item {
          background: var(--bg-primary);
          border-radius: 10px;
          overflow: hidden;
        }
        .exercise-item.done { border-left: 3px solid var(--success); }
        .exercise-header {
          display: flex;
          align-items: center;
          gap: 12px;
          padding: 12px;
          cursor: pointer;
        }
        .exercise-check {
          width: 22px;
          height: 22px;
          border: 2px solid var(--text-muted);
          border-radius: 6px;
          display: flex;
          align-items: center;
          justify-content: center;
          flex-shrink: 0;
        }
        .exercise-check.checked {
          background: var(--success);
          border-color: var(--success);
          color: white;
        }
        .exercise-info { flex: 1; }
        .exercise-name { display: block; font-size: 14px; }
        .exercise-target { font-size: 12px; color: var(--text-muted); }
        .delete-exercise {
          background: none;
          border: none;
          color: var(--text-muted);
          padding: 4px;
          cursor: pointer;
        }
        .delete-exercise:hover { color: var(--error); }
        .sets-tracker {
          padding: 8px 12px 12px;
          border-top: 1px solid var(--bg-surface);
        }
        .set-row {
          display: flex;
          align-items: center;
          gap: 8px;
          padding: 8px 0;
        }
        .set-num { font-size: 12px; color: var(--text-muted); width: 45px; }
        .set-inputs { display: flex; gap: 8px; flex: 1; }
        .set-inputs input {
          flex: 1;
          padding: 8px;
          background: var(--bg-surface);
          border: none;
          border-radius: 6px;
          color: var(--text-primary);
          font-size: 14px;
          text-align: center;
        }
        .add-exercise-form {
          background: var(--bg-primary);
          padding: 12px;
          border-radius: 10px;
          margin-bottom: 12px;
        }
        .add-exercise-form input {
          width: 100%;
          padding: 10px;
          background: var(--bg-surface);
          border: none;
          border-radius: 6px;
          color: var(--text-primary);
          font-size: 13px;
          margin-bottom: 8px;
        }
        .exercise-params {
          display: flex;
          gap: 8px;
          margin-bottom: 10px;
        }
        .exercise-params input { flex: 1; }
        .add-exercise-actions { display: flex; gap: 8px; justify-content: flex-end; }
        .add-exercise-btn {
          display: flex;
          align-items: center;
          justify-content: center;
          gap: 6px;
          width: 100%;
          padding: 12px;
          background: none;
          border: 2px dashed var(--text-muted);
          border-radius: 10px;
          color: var(--text-muted);
          font-size: 13px;
          cursor: pointer;
          margin-bottom: 12px;
        }
        .add-exercise-btn:hover {
          border-color: var(--gold);
          color: var(--gold);
        }
        .day-footer {
          display: flex;
          gap: 10px;
          justify-content: space-between;
        }
        .complete-day-btn {
          display: flex;
          align-items: center;
          gap: 6px;
          background: var(--success);
          border: none;
          padding: 10px 16px;
          border-radius: 8px;
          color: white;
          font-size: 13px;
          font-weight: 600;
          cursor: pointer;
        }
        .delete-day-btn {
          display: flex;
          align-items: center;
          gap: 6px;
          background: rgba(231,76,60,0.15);
          border: none;
          padding: 10px 16px;
          border-radius: 8px;
          color: var(--error);
          font-size: 13px;
          cursor: pointer;
        }
      `}</style>
    </div>
  );
}

export default Workout;
