      children: `
        .workout-page {
          padding: 20px;
          padding-bottom: 100px;
        }
        .page-header {
          display: flex;
          justify-content: space-between;
          align-items: flex-start;
          margin-bottom: 20px;
        }
        .page-header h1 { font-size: 28px; margin-bottom: 4px; }
        .subtitle { color: var(--gold); font-size: 13px; font-weight: 500; }
        .header-actions { display: flex; gap: 8px; align-items: center; }
        .week-badge {
          background: var(--primary);
          padding: 8px 14px;
          border-radius: 20px;
          font-size: 12px;
          font-weight: 600;
        }
        .disable-btn {
          background: var(--bg-surface);
          border: none;
          width: 36px;
          height: 36px;
          border-radius: 10px;
          color: var(--text-muted);
          cursor: pointer;
          display: flex;
          align-items: center;
          justify-content: center;
        }
        .goal-card {
          display: flex;
          align-items: center;
          gap: 12px;
          background: linear-gradient(135deg, var(--primary) 0%, var(--primary-light) 100%);
          padding: 16px;
          border-radius: 12px;
          margin-bottom: 16px;
        }
        .goal-label {
          font-size: 11px;
          color: var(--text-secondary);
          text-transform: uppercase;
          display: block;
        }
        .goal-text { font-size: 14px; font-weight: 600; }

        .custom-pdf-section {
          background: var(--bg-surface);
          border-radius: 12px;
          padding: 14px;
          margin-bottom: 12px;
        }
        .pdf-header {
          display: flex;
          align-items: center;
          gap: 10px;
          margin-bottom: 12px;
          font-size: 14px;
          font-weight: 500;
        }
        .pro-badge {
          display: flex;
          align-items: center;
          gap: 4px;
          background: #8b5cf6;
          color: white;
          padding: 2px 8px;
          border-radius: 10px;
          font-size: 10px;
          margin-left: auto;
        }
        .pdf-uploaded {
          display: flex;
          align-items: center;
          justify-content: space-between;
          padding: 12px;
          background: var(--bg-surface-light);
          border-radius: 8px;
        }
        .pdf-name {
          font-size: 13px;
          color: var(--text-secondary);
        }
        .remove-pdf-btn {
          background: rgba(231,76,60,0.2);
          border: none;
          width: 32px;
          height: 32px;
          border-radius: 8px;
          color: var(--error);
          cursor: pointer;
          display: flex;
          align-items: center;
          justify-content: center;
        }
        .upload-pdf-btn {
          display: flex;
          align-items: center;
          justify-content: center;
          gap: 8px;
          width: 100%;
          padding: 14px;
          background: var(--bg-surface-light);
          border: 2px dashed var(--text-muted);
          border-radius: 10px;
          color: var(--text-secondary);
          font-size: 13px;
          cursor: pointer;
        }
        .upload-pdf-btn:hover {
          border-color: var(--gold);
          color: var(--gold);
        }

        .weight-tracker {
          background: var(--bg-surface);
          border-radius: 14px;
          padding: 16px;
          margin-bottom: 16px;
        }
        .weight-header {
          display: flex;
          align-items: center;
          gap: 10px;
          margin-bottom: 14px;
        }
        .weight-header h3 { flex: 1; font-size: 15px; }
        .log-weight-btn {
          background: var(--primary);
          border: none;
          padding: 8px 14px;
          border-radius: 8px;
          color: var(--gold);
          font-size: 12px;
          font-weight: 600;
          cursor: pointer;
        }
        .weight-input-row {
          display: flex;
          align-items: center;
          gap: 10px;
          margin-bottom: 14px;
        }
        .weight-input-row input {
          flex: 1;
          padding: 12px;
          background: var(--bg-surface-light);
          border: none;
          border-radius: 8px;
          color: var(--text-primary);
          font-size: 16px;
        }
        .weight-input-row span { color: var(--text-muted); }
        .save-weight-btn {
          background: var(--gold);
          border: none;
          padding: 12px 20px;
          border-radius: 8px;
          color: var(--bg-primary);
          font-weight: 600;
          cursor: pointer;
        }
        .weight-stats {
          display: grid;
          grid-template-columns: repeat(3, 1fr);
          gap: 10px;
        }
        .weight-stat {
          text-align: center;
          padding: 12px;
          background: var(--bg-surface-light);
          border-radius: 10px;
        }
        .weight-value {
          display: block;
          font-size: 22px;
          font-weight: 700;
          color: var(--gold);
        }
        .weight-label { font-size: 11px; color: var(--text-muted); }
        .weight-trend {
          display: flex;
          align-items: center;
          justify-content: center;
          gap: 6px;
          margin-top: 12px;
          font-size: 12px;
          color: var(--success);
        }
        .weight-locked {
          display: flex;
          align-items: center;
          gap: 8px;
          padding: 14px;
          background: var(--bg-surface);
          border-radius: 12px;
          margin-bottom: 16px;
          font-size: 13px;
          color: var(--text-muted);
        }
        .weight-locked a {
          margin-left: auto;
          color: var(--gold);
          text-decoration: none;
          font-weight: 600;
        }

        .today-workout {
          background: var(--bg-surface);
          border-radius: 16px;
          padding: 20px;
          margin-bottom: 16px;
          border-left: 4px solid var(--gold);
        }
        .today-workout.completed { border-left-color: var(--success); }
        .today-header {
          display: flex;
          justify-content: space-between;
          align-items: flex-start;
          margin-bottom: 16px;
        }
        .today-header h2 { font-size: 18px; margin-bottom: 4px; }
        .today-header p { color: var(--text-muted); font-size: 13px; }
        .today-progress {
          background: var(--primary);
          padding: 6px 12px;
          border-radius: 20px;
          font-size: 13px;
          font-weight: 600;
          color: var(--gold);
        }
        .today-exercises-list {
          display: flex;
          flex-direction: column;
          gap: 8px;
          margin-bottom: 16px;
        }
        .exercise-item {
          background: var(--bg-surface-light);
          border-radius: 12px;
          overflow: hidden;
        }
        .exercise-item.done { border-left: 3px solid var(--success); }
        .exercise-header {
          display: flex;
          align-items: center;
          gap: 12px;
          padding: 14px;
          cursor: pointer;
        }
        .exercise-check {
          width: 24px;
          height: 24px;
          border: 2px solid var(--text-muted);
          border-radius: 6px;
          display: flex;
          align-items: center;
          justify-content: center;
          flex-shrink: 0;
        }
        .exercise-check.checked {
          background: var(--success);
          border-color: var(--success);
          color: white;
        }
        .exercise-info { flex: 1; }
        .exercise-name { display: block; font-size: 14px; font-weight: 500; }
        .exercise-target { font-size: 12px; color: var(--text-muted); }
        .sets-tracker {
          padding: 0 14px 14px;
          border-top: 1px solid var(--bg-primary);
        }
        .set-row {
          display: flex;
          align-items: center;
          gap: 8px;
          padding: 10px 0;
          border-bottom: 1px solid var(--bg-primary);
        }
        .set-row:last-child { border-bottom: none; }
        .set-num { font-size: 12px; color: var(--text-muted); width: 45px; }
        .set-inputs { display: flex; gap: 8px; flex: 1; }
        .set-inputs input {
          flex: 1;
          padding: 8px;
          background: var(--bg-primary);
          border: none;
          border-radius: 6px;
          color: var(--text-primary);
          font-size: 14px;
          text-align: center;
        }
        .complete-day-btn {
          width: 100%;
          display: flex;
          align-items: center;
          justify-content: center;
          gap: 8px;
          padding: 16px;
          border: none;
          border-radius: 12px;
          font-weight: 600;
          font-size: 14px;
          cursor: pointer;
        }
        .complete-day-btn.ready {
          background: var(--gold);
          color: var(--bg-primary);
        }
        .complete-day-btn.disabled {
          background: var(--bg-surface-light);
          color: var(--text-muted);
          cursor: not-allowed;
        }
        .day-completed-badge {
          display: flex;
          align-items: center;
          justify-content: center;
          gap: 8px;
          padding: 16px;
          background: rgba(46,204,113,0.2);
          border-radius: 12px;
          color: var(--success);
          font-weight: 600;
        }
        .weekly-progress {
          background: var(--bg-surface);
          padding: 16px;
          border-radius: 12px;
          margin-bottom: 16px;
          text-align: center;
        }
        .weekly-progress h3 {
          font-size: 14px;
          color: var(--text-secondary);
          margin-bottom: 12px;
        }
        .progress-dots {
          display: flex;
          justify-content: center;
          gap: 8px;
          margin-bottom: 8px;
        }
        .day-dot {
          width: 40px;
          height: 40px;
          border-radius: 10px;
          background: var(--bg-surface-light);
          display: flex;
          align-items: center;
          justify-content: center;
          font-size: 18px;
          opacity: 0.5;
        }
        .day-dot.today {
          border: 2px solid var(--gold);
          opacity: 1;
        }
        .day-dot.completed {
          opacity: 1;
          background: var(--primary);
        }
        .progress-text { font-size: 12px; color: var(--text-muted); }
        .nutrition-toggle {
          width: 100%;
          display: flex;
          align-items: center;
          gap: 10px;
          padding: 14px 16px;
          background: var(--bg-surface);
          border: none;
          border-radius: 12px;
          color: var(--text-primary);
          font-size: 14px;
          cursor: pointer;
          margin-bottom: 12px;
        }
        .nutrition-toggle span { flex: 1; text-align: left; }
        .nutrition-card {
          display: grid;
          grid-template-columns: repeat(4, 1fr);
          gap: 8px;
          margin-bottom: 20px;
        }
        .macro {
          background: var(--bg-surface);
          padding: 12px;
          border-radius: 12px;
          text-align: center;
        }
        .macro-value {
          display: block;
          font-size: 16px;
          font-weight: 700;
          color: var(--gold);
        }
        .macro-label { font-size: 10px; color: var(--text-muted); }
