          padding: 20px;
          color: var(--text-muted);
          font-size: 13px;
        }
        .exercises-list {
          display: flex;
          flex-direction: column;
          gap: 8px;
          margin-bottom: 12px;
        }
        .exercise-item {
          background: var(--bg-primary);
          border-radius: 10px;
          overflow: hidden;
        }
        .exercise-item.done { border-left: 3px solid var(--success); }
        .exercise-header {
          display: flex;
          align-items: center;
          gap: 12px;
          padding: 12px;
          cursor: pointer;
        }
        .exercise-check {
          width: 22px;
          height: 22px;
          border: 2px solid var(--text-muted);
          border-radius: 6px;
          display: flex;
          align-items: center;
          justify-content: center;
          flex-shrink: 0;
        }
        .exercise-check.checked {
          background: var(--success);
          border-color: var(--success);
          color: white;
        }
        .exercise-info { flex: 1; }
        .exercise-name { display: block; font-size: 14px; }
        .exercise-target { font-size: 12px; color: var(--text-muted); }
        .delete-exercise {
          background: none;
          border: none;
          color: var(--text-muted);
          padding: 4px;
          cursor: pointer;
        }
        .delete-exercise:hover { color: var(--error); }
        .sets-tracker {
          padding: 8px 12px 12px;
          border-top: 1px solid var(--bg-surface);
        }
        .set-row {
          display: flex;
          align-items: center;
          gap: 8px;
          padding: 8px 0;
        }
        .set-num { font-size: 12px; color: var(--text-muted); width: 45px; }
        .set-inputs { display: flex; gap: 8px; flex: 1; }
        .set-inputs input {
          flex: 1;
          padding: 8px;
          background: var(--bg-surface);
          border: none;
          border-radius: 6px;
          color: var(--text-primary);
          font-size: 14px;
          text-align: center;
        }
        .add-exercise-form {
          background: var(--bg-primary);
          padding: 12px;
          border-radius: 10px;
          margin-bottom: 12px;
        }
        .add-exercise-form input {
          width: 100%;
          padding: 10px;
          background: var(--bg-surface);
          border: none;
          border-radius: 6px;
          color: var(--text-primary);
          font-size: 13px;
          margin-bottom: 8px;
        }
        .exercise-params {
          display: flex;
          gap: 8px;
          margin-bottom: 10px;
        }
        .exercise-params input { flex: 1; }
        .add-exercise-actions { display: flex; gap: 8px; justify-content: flex-end; }
        .add-exercise-btn {
          display: flex;
          align-items: center;
          justify-content: center;
          gap: 6px;
          width: 100%;
          padding: 12px;
          background: none;
          border: 2px dashed var(--text-muted);
          border-radius: 10px;
          color: var(--text-muted);
          font-size: 13px;
          cursor: pointer;
          margin-bottom: 12px;
        }
        .add-exercise-btn:hover {
          border-color: var(--gold);
          color: var(--gold);
        }
        .day-footer {
          display: flex;
          gap: 10px;
          justify-content: space-between;
        }
        .complete-day-btn {
          display: flex;
          align-items: center;
          gap: 6px;
          background: var(--success);
          border: none;
          padding: 10px 16px;
          border-radius: 8px;
          color: white;
          font-size: 13px;
          font-weight: 600;
          cursor: pointer;
        }
        .delete-day-btn {
          display: flex;
          align-items: center;
          gap: 6px;
          background: rgba(231,76,60,0.15);
          border: none;
          padding: 10px 16px;
          border-radius: 8px;
          color: var(--error);
          font-size: 13px;
          cursor: pointer;
        }
        .loading-state {
          display: flex;
          flex-direction: column;
          align-items: center;
          justify-content: center;
          min-height: 300px;
        }
        .spinner {
          width: 40px;
          height: 40px;
          border: 3px solid var(--bg-surface-light);
          border-top-color: var(--gold);
          border-radius: 50%;
          animation: spin 1s linear infinite;
          margin-bottom: 16px;
        }
        @keyframes spin { to { transform: rotate(360deg); } }
      `}</style>
    </div>
  );
}

export default Workout;
