import React, { createContext, useContext, useState, useEffect } from 'react';
import { format } from 'date-fns';

const AppContext = createContext();

export function AppProvider({ children }) {
  const [user, setUser] = useState(null);
  const [prayerTimes, setPrayerTimes] = useState(null);
  const [location, setLocation] = useState(null);

  const [xp, setXp] = useState(() => {
    const saved = localStorage.getItem('qawaam_xp');
    return saved ? parseInt(saved) : 0;
  });

  const [streak, setStreak] = useState(() => {
    const saved = localStorage.getItem('qawaam_streak');
    return saved ? parseInt(saved) : 0;
  });

  const [habits, setHabits] = useState(() => {
    const saved = localStorage.getItem('qawaam_habits');
    const today = new Date().toDateString();
    if (saved) {
      const parsed = JSON.parse(saved);
      // Reset habits if it's a new day
      if (parsed.date !== today) {
        return {
          date: today,
          items: (parsed.items || parsed).map(h => ({ ...h, completed: false }))
        };
      }
      return parsed;
    }
    return {
      date: today,
      items: [
        { id: '1', name: 'Wake up for Fajr', completed: false },
        { id: '2', name: 'Read Quran', completed: false },
        { id: '3', name: 'Morning Adhkar', completed: false },
        { id: '4', name: 'Evening Adhkar', completed: false },
        { id: '5', name: 'Drink 8 glasses of water', completed: false },
      ]
    };
  });

  // Get today's prayers from the all prayers storage
  const [prayers, setPrayers] = useState(() => {
    const saved = localStorage.getItem('qawaam_all_prayers');
    if (saved) {
      const allPrayers = JSON.parse(saved);
      const today = format(new Date(), 'yyyy-MM-dd');
      return allPrayers[today] || {};
    }
    return {};
  });

  // Save to localStorage
  useEffect(() => {
    localStorage.setItem('qawaam_xp', xp.toString());
  }, [xp]);

  useEffect(() => {
    localStorage.setItem('qawaam_streak', streak.toString());
  }, [streak]);

  useEffect(() => {
    localStorage.setItem('qawaam_habits', JSON.stringify(habits));
  }, [habits]);

  // Sync prayers from all_prayers storage
  useEffect(() => {
    const handleStorageChange = () => {
      const saved = localStorage.getItem('qawaam_all_prayers');
      if (saved) {
        const allPrayers = JSON.parse(saved);
        const today = format(new Date(), 'yyyy-MM-dd');
        setPrayers(allPrayers[today] || {});
      }
    };

    // Check every second for updates (since storage event doesn't fire in same tab)
    const interval = setInterval(handleStorageChange, 1000);
    return () => clearInterval(interval);
  }, []);

  // Get location on mount
  useEffect(() => {
    if (navigator.geolocation) {
      navigator.geolocation.getCurrentPosition(
        (pos) => {
          setLocation({
            latitude: pos.coords.latitude,
            longitude: pos.coords.longitude
          });
        },
        (err) => {
          // Default to Mecca if location denied
          setLocation({ latitude: 21.4225, longitude: 39.8262 });
        }
      );
    }
  }, []);

  // Check and update streak
  useEffect(() => {
    const lastActivity = localStorage.getItem('qawaam_last_activity');
    const today = new Date().toDateString();
    const yesterday = new Date(Date.now() - 86400000).toDateString();

    if (lastActivity === yesterday) {
      // Continuing streak
    } else if (lastActivity !== today) {
      // Streak broken (more than 1 day gap)
      if (lastActivity && lastActivity !== yesterday) {
        setStreak(0);
      }
    }
  }, []);

  const addXp = (amount) => {
    setXp(prev => prev + amount);
    // Update last activity for streak
    localStorage.setItem('qawaam_last_activity', new Date().toDateString());
  };

  const markPrayer = (prayer, status) => {
    const today = format(new Date(), 'yyyy-MM-dd');
    const saved = localStorage.getItem('qawaam_all_prayers');
    const allPrayers = saved ? JSON.parse(saved) : {};

    allPrayers[today] = {
      ...allPrayers[today],
      [prayer]: status
    };

    localStorage.setItem('qawaam_all_prayers', JSON.stringify(allPrayers));
    setPrayers(allPrayers[today]);

    if (status === 'on_time') addXp(50);
    else if (status === 'late') addXp(20);
  };

  const toggleHabit = (habitId) => {
    setHabits(prev => ({
      ...prev,
      items: prev.items.map(h => {
        if (h.id === habitId) {
          if (!h.completed) addXp(10);
          return { ...h, completed: !h.completed };
        }
        return h;
      })
    }));
  };

  const addHabit = (name) => {
    setHabits(prev => ({
      ...prev,
      items: [...prev.items, { id: Date.now().toString(), name, completed: false }]
    }));
  };

  const deleteHabit = (habitId) => {
    setHabits(prev => ({
      ...prev,
      items: prev.items.filter(h => h.id !== habitId)
    }));
  };

  const getLevel = () => {
    if (xp < 500) return { name: 'Beginner', level: 1, nextXp: 500 };
    if (xp < 1500) return { name: 'Steadfast', level: 2, nextXp: 1500 };
    if (xp < 3500) return { name: 'Consistent', level: 3, nextXp: 3500 };
    if (xp < 7500) return { name: 'Firm', level: 4, nextXp: 7500 };
    return { name: 'Master', level: 5, nextXp: null };
  };

  // Get habits items array for compatibility
  const habitsArray = habits.items || habits;

  return (
    <AppContext.Provider value={{
      user, setUser,
      prayerTimes, setPrayerTimes,
      location, setLocation,
      xp, addXp,
      streak, setStreak,
      habits: habitsArray, 
      setHabits: (items) => setHabits(prev => ({ ...prev, items })),
      toggleHabit,
      addHabit,
      deleteHabit,
      prayers, markPrayer,
      getLevel,
    }}>
      {children}
    </AppContext.Provider>
  );
}

export const useApp = () => useContext(AppContext);
