import React, { useState, useEffect } from 'react';
import { useAuth } from '../context/AuthContext';
import { doc, getDoc, setDoc } from 'firebase/firestore';
import { db } from '../config/firebase';
import { 
  RotateCcw, Check, ChevronRight, BookOpen, 
  Calendar, AlertCircle, Clock, Target
} from 'lucide-react';
import { SURAHS } from './QuranReader';

// Spaced repetition intervals (in days)
const REVIEW_SCHEDULE = [
  { days: 1, label: 'Tomorrow' },
  { days: 3, label: '3 days' },
  { days: 7, label: '1 week' },
  { days: 14, label: '2 weeks' },
  { days: 30, label: '1 month' },
  { days: 60, label: '2 months' },
];

function RevisionPlan() {
  const { user } = useAuth();

  const [surveyDone, setSurveyDone] = useState(false);
  const [surveyStep, setSurveyStep] = useState(0);
  const [surveyData, setSurveyData] = useState({
    juz1_5: 'none',
    juz6_10: 'none',
    juz11_15: 'none',
    juz16_20: 'none',
    juz21_25: 'none',
    juz26_30: 'none',
    dailyReviewTime: 30, // minutes
  });

  const [revisionPlan, setRevisionPlan] = useState([]);
  const [todayReviews, setTodayReviews] = useState([]);
  const [loading, setLoading] = useState(true);

  const KNOWLEDGE_LEVELS = [
    { value: 'none', label: 'Not memorized', color: 'var(--text-muted)' },
    { value: 'weak', label: 'Weak - Need help', color: 'var(--error)' },
    { value: 'moderate', label: 'Moderate - Some mistakes', color: 'var(--warning)' },
    { value: 'strong', label: 'Strong - Mostly fluent', color: 'var(--info)' },
    { value: 'mastered', label: 'Mastered - Very fluent', color: 'var(--success)' },
  ];

  const JUZ_RANGES = [
    { key: 'juz1_5', label: 'Juz 1-5', surahs: '1-5' },
    { key: 'juz6_10', label: 'Juz 6-10', surahs: '6-9' },
    { key: 'juz11_15', label: 'Juz 11-15', surahs: '10-15' },
    { key: 'juz16_20', label: 'Juz 16-20', surahs: '16-18' },
    { key: 'juz21_25', label: 'Juz 21-25', surahs: '19-25' },
    { key: 'juz26_30', label: 'Juz 26-30', surahs: '78-114' },
  ];

  useEffect(() => {
    if (user) {
      loadRevisionData();
    }
  }, [user]);

  const loadRevisionData = async () => {
    setLoading(true);
    try {
      const docRef = doc(db, 'users', user.uid, 'quran', 'revision');
      const docSnap = await getDoc(docRef);

      if (docSnap.exists()) {
        const data = docSnap.data();
        setSurveyDone(data.surveyCompleted || false);
        setSurveyData(data.surveyData || surveyData);
        setRevisionPlan(data.plan || []);

        // Filter today's reviews
        const today = new Date().toDateString();
        const dueReviews = (data.plan || []).filter(item => {
          if (!item.nextReview) return false;
          const reviewDate = new Date(item.nextReview);
          return reviewDate <= new Date();
        });
        setTodayReviews(dueReviews);
      }
    } catch (error) {
      console.error('Error loading revision data:', error);
    }
    setLoading(false);
  };

  const generatePlan = () => {
    const plan = [];

    JUZ_RANGES.forEach((juzRange) => {
      const level = surveyData[juzRange.key];
      if (level === 'none') return;

      // Calculate review frequency based on knowledge level
      let reviewInterval;
      switch (level) {
        case 'weak': reviewInterval = 1; break;
        case 'moderate': reviewInterval = 3; break;
        case 'strong': reviewInterval = 7; break;
        case 'mastered': reviewInterval = 14; break;
        default: reviewInterval = 7;
      }

      const nextReview = new Date();
      nextReview.setDate(nextReview.getDate() + reviewInterval);

      plan.push({
        id: juzRange.key,
        label: juzRange.label,
        surahs: juzRange.surahs,
        level,
        reviewCount: 0,
        nextReview: nextReview.toISOString(),
        lastReviewed: null
      });
    });

    return plan;
  };

  const saveSurvey = async () => {
    const plan = generatePlan();

    try {
      await setDoc(doc(db, 'users', user.uid, 'quran', 'revision'), {
        surveyCompleted: true,
        surveyData,
        plan,
        updatedAt: new Date().toISOString()
      });

      setRevisionPlan(plan);
      setSurveyDone(true);

      // Set today's reviews
      const dueReviews = plan.filter(item => {
        const reviewDate = new Date(item.nextReview);
        return reviewDate <= new Date();
      });
      setTodayReviews(dueReviews);
    } catch (error) {
      console.error('Error saving survey:', error);
    }
  };

  const markReviewed = async (itemId, quality) => {
    // quality: 'easy', 'medium', 'hard'
    const updatedPlan = revisionPlan.map(item => {
      if (item.id === itemId) {
        let nextInterval;
        const newReviewCount = item.reviewCount + 1;

        switch (quality) {
          case 'easy':
            nextInterval = REVIEW_SCHEDULE[Math.min(newReviewCount, REVIEW_SCHEDULE.length - 1)].days;
            break;
          case 'medium':
            nextInterval = REVIEW_SCHEDULE[Math.min(newReviewCount - 1, REVIEW_SCHEDULE.length - 1)].days;
            break;
          case 'hard':
            nextInterval = 1; // Review again tomorrow
            break;
          default:
            nextInterval = 3;
        }

        const nextReview = new Date();
        nextReview.setDate(nextReview.getDate() + nextInterval);

        return {
          ...item,
          reviewCount: quality === 'hard' ? item.reviewCount : newReviewCount,
          nextReview: nextReview.toISOString(),
          lastReviewed: new Date().toISOString()
        };
      }
      return item;
    });

    setRevisionPlan(updatedPlan);
    setTodayReviews(prev => prev.filter(item => item.id !== itemId));

    // Save to Firestore
    try {
      await setDoc(doc(db, 'users', user.uid, 'quran', 'revision'), {
        surveyCompleted: true,
        surveyData,
        plan: updatedPlan,
        updatedAt: new Date().toISOString()
      }, { merge: true });
    } catch (error) {
      console.error('Error saving review:', error);
    }
  };

  if (loading) {
    return (
      <div className="loading-state">
        <div className="spinner" />
        <p>Loading revision plan...</p>
      </div>
    );
  }

  // Survey View
  if (!surveyDone) {
    const currentJuz = JUZ_RANGES[surveyStep];

    return (
      <div className="revision-survey">
        <div className="survey-header">
          <RotateCcw size={32} className="icon" />
          <h2>Knowledge Survey</h2>
          <p>Tell us what you've memorized so we can create your personalized revision plan</p>
        </div>

        <div className="progress-indicator">
          {JUZ_RANGES.map((_, i) => (
            <span 
              key={i} 
              className={`dot ${i < surveyStep ? 'done' : i === surveyStep ? 'current' : ''}`}
            />
          ))}
        </div>

        <div className="survey-question">
          <h3>How well do you know {currentJuz.label}?</h3>
          <p className="surah-range">Surahs {currentJuz.surahs}</p>

          <div className="options">
            {KNOWLEDGE_LEVELS.map((level) => (
              <button
                key={level.value}
                className={`option ${surveyData[currentJuz.key] === level.value ? 'selected' : ''}`}
                onClick={() => setSurveyData(prev => ({ ...prev, [currentJuz.key]: level.value }))}
                style={{ borderColor: surveyData[currentJuz.key] === level.value ? level.color : 'transparent' }}
              >
                <span className="option-dot" style={{ background: level.color }} />
                <span>{level.label}</span>
              </button>
            ))}
          </div>
        </div>

        <div className="survey-nav">
          {surveyStep > 0 && (
            <button className="btn btn-secondary" onClick={() => setSurveyStep(surveyStep - 1)}>
              Back
            </button>
          )}

          {surveyStep < JUZ_RANGES.length - 1 ? (
            <button className="btn btn-primary" onClick={() => setSurveyStep(surveyStep + 1)}>
              Next <ChevronRight size={18} />
            </button>
          ) : (
            <button className="btn btn-primary" onClick={saveSurvey}>
              <Target size={18} /> Generate Plan
            </button>
          )}
        </div>

        <style>{`
          .revision-survey { animation: fadeIn 0.3s ease-out; }
          .survey-header {
            text-align: center;
            padding: 30px 20px;
            background: var(--bg-surface);
            border-radius: 16px;
            margin-bottom: 20px;
          }
          .survey-header .icon { color: var(--gold); margin-bottom: 12px; }
          .survey-header h2 { font-size: 22px; margin-bottom: 8px; }
          .survey-header p { color: var(--text-muted); font-size: 14px; }
          .progress-indicator {
            display: flex;
            justify-content: center;
            gap: 8px;
            margin-bottom: 24px;
          }
          .dot {
            width: 10px;
            height: 10px;
            border-radius: 50%;
            background: var(--bg-surface-light);
          }
          .dot.done { background: var(--success); }
          .dot.current { background: var(--gold); }
          .survey-question {
            background: var(--bg-surface);
            padding: 24px;
            border-radius: 16px;
            margin-bottom: 20px;
          }
          .survey-question h3 { font-size: 18px; margin-bottom: 8px; }
          .surah-range { color: var(--text-muted); font-size: 13px; margin-bottom: 20px; }
          .options { display: flex; flex-direction: column; gap: 10px; }
          .option {
            display: flex;
            align-items: center;
            gap: 12px;
            padding: 14px 16px;
            background: var(--bg-surface-light);
            border: 2px solid transparent;
            border-radius: 12px;
            cursor: pointer;
            transition: all 0.2s ease;
            font-size: 14px;
            color: var(--text-primary);
          }
          .option:hover { background: var(--bg-primary); }
          .option.selected { background: var(--bg-primary); }
          .option-dot { width: 12px; height: 12px; border-radius: 50%; }
          .survey-nav { display: flex; justify-content: flex-end; gap: 12px; }
          .survey-nav .btn { padding: 14px 24px; }
        `}</style>
      </div>
    );
  }

  // Revision Dashboard
  return (
    <div className="revision-dashboard">
      {/* Today's Reviews */}
      <div className="today-reviews">
        <div className="section-header">
          <Calendar size={18} />
          <h3>Today's Reviews</h3>
          <span className="count">{todayReviews.length} due</span>
        </div>

        {todayReviews.length === 0 ? (
          <div className="no-reviews">
            <Check size={32} />
            <p>All caught up! No reviews due today.</p>
          </div>
        ) : (
          <div className="review-items">
            {todayReviews.map((item) => (
              <div key={item.id} className="review-item">
                <div className="review-info">
                  <h4>{item.label}</h4>
                  <span className="surah-info">Surahs {item.surahs}</span>
                </div>

                <div className="review-buttons">
                  <button 
                    className="quality-btn hard"
                    onClick={() => markReviewed(item.id, 'hard')}
                  >
                    Hard
                  </button>
                  <button 
                    className="quality-btn medium"
                    onClick={() => markReviewed(item.id, 'medium')}
                  >
                    Medium
                  </button>
                  <button 
                    className="quality-btn easy"
                    onClick={() => markReviewed(item.id, 'easy')}
                  >
                    Easy
                  </button>
                </div>
              </div>
            ))}
          </div>
        )}
      </div>

      {/* Upcoming Reviews */}
      <div className="upcoming-reviews">
        <div className="section-header">
          <Clock size={18} />
          <h3>Upcoming Reviews</h3>
        </div>

        <div className="schedule-list">
          {revisionPlan
            .filter(item => new Date(item.nextReview) > new Date())
            .sort((a, b) => new Date(a.nextReview) - new Date(b.nextReview))
            .slice(0, 5)
            .map((item) => {
              const daysUntil = Math.ceil((new Date(item.nextReview) - new Date()) / (1000 * 60 * 60 * 24));
              return (
                <div key={item.id} className="schedule-item">
                  <BookOpen size={16} />
                  <div className="schedule-info">
                    <span className="schedule-label">{item.label}</span>
                    <span className="schedule-date">
                      {daysUntil === 1 ? 'Tomorrow' : `In ${daysUntil} days`}
                    </span>
                  </div>
                </div>
              );
            })}
        </div>
      </div>

      {/* Retake Survey */}
      <button 
        className="retake-btn"
        onClick={() => { setSurveyDone(false); setSurveyStep(0); }}
      >
        <AlertCircle size={16} />
        Update Knowledge Survey
      </button>

      <style>{`
        .revision-dashboard { animation: fadeIn 0.3s ease-out; }
        .section-header {
          display: flex;
          align-items: center;
          gap: 10px;
          margin-bottom: 16px;
        }
        .section-header h3 { flex: 1; font-size: 16px; }
        .count {
          background: var(--gold);
          color: var(--bg-primary);
          padding: 4px 12px;
          border-radius: 20px;
          font-size: 12px;
          font-weight: 600;
        }
        .today-reviews {
          background: var(--bg-surface);
          padding: 20px;
          border-radius: 16px;
          margin-bottom: 16px;
        }
        .no-reviews {
          text-align: center;
          padding: 30px;
        }
        .no-reviews svg { color: var(--success); margin-bottom: 12px; }
        .no-reviews p { color: var(--text-muted); font-size: 14px; }
        .review-items { display: flex; flex-direction: column; gap: 12px; }
        .review-item {
          display: flex;
          flex-direction: column;
          gap: 12px;
          padding: 16px;
          background: var(--bg-surface-light);
          border-radius: 12px;
        }
        .review-info h4 { font-size: 15px; margin-bottom: 4px; }
        .surah-info { font-size: 12px; color: var(--text-muted); }
        .review-buttons { display: grid; grid-template-columns: repeat(3, 1fr); gap: 8px; }
        .quality-btn {
          padding: 10px;
          border: none;
          border-radius: 8px;
          font-size: 13px;
          font-weight: 600;
          cursor: pointer;
        }
        .quality-btn.hard { background: rgba(231,76,60,0.2); color: var(--error); }
        .quality-btn.medium { background: rgba(241,196,15,0.2); color: var(--warning); }
        .quality-btn.easy { background: rgba(46,204,113,0.2); color: var(--success); }
        .upcoming-reviews {
          background: var(--bg-surface);
          padding: 20px;
          border-radius: 16px;
          margin-bottom: 16px;
        }
        .schedule-list { display: flex; flex-direction: column; gap: 10px; }
        .schedule-item {
          display: flex;
          align-items: center;
          gap: 12px;
          padding: 12px;
          background: var(--bg-surface-light);
          border-radius: 10px;
        }
        .schedule-item svg { color: var(--gold); }
        .schedule-info { flex: 1; }
        .schedule-label { display: block; font-size: 14px; }
        .schedule-date { font-size: 12px; color: var(--text-muted); }
        .retake-btn {
          display: flex;
          align-items: center;
          justify-content: center;
          gap: 8px;
          width: 100%;
          padding: 14px;
          background: var(--bg-surface);
          border: none;
          border-radius: 12px;
          color: var(--text-muted);
          font-size: 13px;
          cursor: pointer;
        }
        .retake-btn:hover { color: var(--gold); }
        .loading-state {
          display: flex;
          flex-direction: column;
          align-items: center;
          justify-content: center;
          padding: 60px;
        }
        .spinner {
          width: 40px;
          height: 40px;
          border: 3px solid var(--bg-surface-light);
          border-top-color: var(--gold);
          border-radius: 50%;
          animation: spin 1s linear infinite;
          margin-bottom: 16px;
        }
        @keyframes spin { to { transform: rotate(360deg); } }
        @keyframes fadeIn { from { opacity: 0; } to { opacity: 1; } }
      `}</style>
    </div>
  );
}

export default RevisionPlan;
