import React, { useState, useEffect } from 'react';
import { useAuth } from '../context/AuthContext';
import { doc, setDoc, getDoc, updateDoc } from 'firebase/firestore';
import { db } from '../config/firebase';
import { 
  Target, Check, Settings, BookOpen, Award, Flame, 
  ChevronRight, ChevronLeft, Plus, Minus, Calendar, Star
} from 'lucide-react';
import { SURAHS } from './QuranReader';

// Spaced repetition intervals (in days)
const REVIEW_INTERVALS = [1, 3, 7, 14, 30];

function MemorizationPlan() {
  const { user, userProfile, updateUserProfile } = useAuth();

  const [settings, setSettings] = useState({
    dailyNewAyats: 5,
    dailyReviewAyats: 10,
    currentSurah: 114,
    currentAyah: 1,
    startedDate: null
  });

  const [progress, setProgress] = useState({
    memorizedAyats: [],
    todayNew: 0,
    todayReviewed: 0,
    lastActiveDate: null,
    streak: 0,
    points: 0
  });

  const [todayAyahs, setTodayAyahs] = useState([]);
  const [reviewAyahs, setReviewAyahs] = useState([]);
  const [loading, setLoading] = useState(true);
  const [view, setView] = useState('dashboard'); // dashboard, memorize, review, settings
  const [currentAyahIndex, setCurrentAyahIndex] = useState(0);

  // Load user's memorization data from Firestore
  useEffect(() => {
    if (user) {
      loadUserData();
    }
  }, [user]);

  const loadUserData = async () => {
    setLoading(true);
    try {
      const docRef = doc(db, 'users', user.uid, 'quran', 'memorization');
      const docSnap = await getDoc(docRef);

      if (docSnap.exists()) {
        const data = docSnap.data();
        setSettings(data.settings || settings);
        setProgress(data.progress || progress);

        // Check if new day
        const today = new Date().toDateString();
        if (data.progress?.lastActiveDate !== today) {
          // Reset daily counters
          setProgress(prev => ({
            ...prev,
            todayNew: 0,
            todayReviewed: 0,
            lastActiveDate: today
          }));
        }
      }
    } catch (error) {
      console.error('Error loading memorization data:', error);
    }
    setLoading(false);
  };

  const saveUserData = async (newSettings, newProgress) => {
    if (!user) return;
    try {
      const docRef = doc(db, 'users', user.uid, 'quran', 'memorization');
      await setDoc(docRef, {
        settings: newSettings || settings,
        progress: newProgress || progress,
        updatedAt: new Date().toISOString()
      }, { merge: true });
    } catch (error) {
      console.error('Error saving memorization data:', error);
    }
  };

  // Get ayahs due for review (spaced repetition)
  const getReviewAyahs = () => {
    const today = new Date();
    const dueAyahs = progress.memorizedAyats.filter(ayah => {
      if (!ayah.nextReview) return false;
      const reviewDate = new Date(ayah.nextReview);
      return reviewDate <= today;
    });
    return dueAyahs.slice(0, settings.dailyReviewAyats);
  };

  // Calculate next review date based on review count
  const getNextReviewDate = (reviewCount) => {
    const interval = REVIEW_INTERVALS[Math.min(reviewCount, REVIEW_INTERVALS.length - 1)];
    const nextDate = new Date();
    nextDate.setDate(nextDate.getDate() + interval);
    return nextDate.toISOString();
  };

  // Mark ayah as memorized
  const markAyahMemorized = async (surah, ayahNum, ayahText) => {
    const ayahId = `${surah}:${ayahNum}`;
    const existingIndex = progress.memorizedAyats.findIndex(a => a.id === ayahId);

    let updatedAyats = [...progress.memorizedAyats];

    if (existingIndex === -1) {
      // New memorization
      updatedAyats.push({
        id: ayahId,
        surah,
        ayahNum,
        text: ayahText.substring(0, 50), // Store preview
        memorizedAt: new Date().toISOString(),
        reviewCount: 0,
        nextReview: getNextReviewDate(0),
        status: 'memorized'
      });
    }

    const newProgress = {
      ...progress,
      memorizedAyats: updatedAyats,
      todayNew: progress.todayNew + 1,
      points: progress.points + 10,
      lastActiveDate: new Date().toDateString()
    };

    setProgress(newProgress);
    await saveUserData(settings, newProgress);

    // Check daily goal
    checkDailyGoal(newProgress);
  };

  // Mark ayah as reviewed
  const markAyahReviewed = async (ayahId, correct) => {
    const updatedAyats = progress.memorizedAyats.map(ayah => {
      if (ayah.id === ayahId) {
        const newReviewCount = correct ? ayah.reviewCount + 1 : Math.max(0, ayah.reviewCount - 1);
        return {
          ...ayah,
          reviewCount: newReviewCount,
          nextReview: getNextReviewDate(newReviewCount),
          lastReviewed: new Date().toISOString()
        };
      }
      return ayah;
    });

    const newProgress = {
      ...progress,
      memorizedAyats: updatedAyats,
      todayReviewed: progress.todayReviewed + 1,
      points: progress.points + (correct ? 5 : 1),
      lastActiveDate: new Date().toDateString()
    };

    setProgress(newProgress);
    await saveUserData(settings, newProgress);

    checkDailyGoal(newProgress);
  };

  // Check if daily goal is met
  const checkDailyGoal = (currentProgress) => {
    const goalMet = currentProgress.todayNew >= settings.dailyNewAyats && 
                    currentProgress.todayReviewed >= settings.dailyReviewAyats;

    if (goalMet) {
      // Update streak
      const newStreak = currentProgress.streak + 1;
      const bonusPoints = 20; // Bonus for completing daily goal

      setProgress(prev => ({
        ...prev,
        streak: newStreak,
        points: prev.points + bonusPoints
      }));
    }
  };

  // Fetch ayahs for memorization
  const fetchNewAyahs = async () => {
    try {
      const response = await fetch(
        `https://api.alquran.cloud/v1/surah/${settings.currentSurah}/ar.alafasy`
      );
      const data = await response.json();
      const surahAyahs = data.data.ayahs;

      // Get next ayahs to memorize
      const startIndex = settings.currentAyah - 1;
      const endIndex = startIndex + settings.dailyNewAyats;
      const newAyahs = surahAyahs.slice(startIndex, endIndex).map(a => ({
        ...a,
        surahNum: settings.currentSurah,
        surahName: SURAHS.find(s => s.number === settings.currentSurah)?.name
      }));

      setTodayAyahs(newAyahs);
    } catch (error) {
      console.error('Error fetching ayahs:', error);
    }
  };

  useEffect(() => {
    if (settings.startedDate && view === 'memorize') {
      fetchNewAyahs();
    }
    if (view === 'review') {
      setReviewAyahs(getReviewAyahs());
    }
  }, [settings, view]);

  const startPlan = async () => {
    const newSettings = {
      ...settings,
      startedDate: new Date().toISOString()
    };
    setSettings(newSettings);
    await saveUserData(newSettings, progress);
    setView('dashboard');
  };

  const dailyGoalProgress = {
    new: Math.min((progress.todayNew / settings.dailyNewAyats) * 100, 100),
    review: Math.min((progress.todayReviewed / settings.dailyReviewAyats) * 100, 100)
  };

  const reviewDueCount = getReviewAyahs().length;

  if (loading) {
    return (
      <div className="loading-state">
        <div className="spinner" />
        <p>Loading your progress...</p>
      </div>
    );
  }

  // Settings View
  if (view === 'settings' || !settings.startedDate) {
    return (
      <div className="memorization-settings">
        <div className="settings-header">
          <Target size={32} className="icon" />
          <h2>{settings.startedDate ? 'Adjust Goals' : 'Set Your Goals'}</h2>
          <p>Customize your daily memorization targets</p>
        </div>

        <div className="setting-option">
          <label>New Ayahs Per Day</label>
          <div className="number-selector">
            <button onClick={() => setSettings(p => ({ ...p, dailyNewAyats: Math.max(1, p.dailyNewAyats - 1) }))}>
              <Minus size={18} />
            </button>
            <span className="number-value">{settings.dailyNewAyats}</span>
            <button onClick={() => setSettings(p => ({ ...p, dailyNewAyats: Math.min(20, p.dailyNewAyats + 1) }))}>
              <Plus size={18} />
            </button>
          </div>
          <span className="helper-text">Minimum: 1, Recommended: 5-10</span>
        </div>

        <div className="setting-option">
          <label>Review Ayahs Per Day</label>
          <div className="number-selector">
            <button onClick={() => setSettings(p => ({ ...p, dailyReviewAyats: Math.max(5, p.dailyReviewAyats - 5) }))}>
              <Minus size={18} />
            </button>
            <span className="number-value">{settings.dailyReviewAyats}</span>
            <button onClick={() => setSettings(p => ({ ...p, dailyReviewAyats: Math.min(50, p.dailyReviewAyats + 5) }))}>
              <Plus size={18} />
            </button>
          </div>
          <span className="helper-text">Minimum: 5, Recommended: 10-20</span>
        </div>

        <div className="setting-option">
          <label>Start From</label>
          <select 
            value={settings.currentSurah}
            onChange={(e) => setSettings(p => ({ 
              ...p, 
              currentSurah: parseInt(e.target.value),
              currentAyah: 1
            }))}
          >
            {SURAHS.slice().reverse().map(surah => (
              <option key={surah.number} value={surah.number}>
                {surah.number}. {surah.name} ({surah.arabicName})
              </option>
            ))}
          </select>
        </div>

        <div className="settings-buttons">
          {settings.startedDate && (
            <button className="btn btn-secondary" onClick={() => setView('dashboard')}>
              Cancel
            </button>
          )}
          <button className="btn btn-primary" onClick={startPlan}>
            <Target size={18} />
            {settings.startedDate ? 'Save Changes' : 'Start Plan'}
          </button>
        </div>

        <style>{`
          .memorization-settings { animation: fadeIn 0.3s ease-out; }
          .settings-header {
            text-align: center;
            padding: 30px 20px;
            background: var(--bg-surface);
            border-radius: 16px;
            margin-bottom: 20px;
          }
          .settings-header .icon { color: var(--gold); margin-bottom: 12px; }
          .settings-header h2 { font-size: 22px; margin-bottom: 8px; }
          .settings-header p { color: var(--text-muted); font-size: 14px; }
          .setting-option {
            background: var(--bg-surface);
            padding: 20px;
            border-radius: 12px;
            margin-bottom: 12px;
          }
          .setting-option label {
            display: block;
            font-size: 14px;
            font-weight: 600;
            margin-bottom: 12px;
          }
          .number-selector {
            display: flex;
            align-items: center;
            justify-content: center;
            gap: 20px;
          }
          .number-selector button {
            width: 44px;
            height: 44px;
            border-radius: 12px;
            background: var(--bg-surface-light);
            border: none;
            color: var(--text-primary);
            cursor: pointer;
            display: flex;
            align-items: center;
            justify-content: center;
          }
          .number-selector button:hover {
            background: var(--primary);
            color: var(--gold);
          }
          .number-value {
            font-size: 32px;
            font-weight: 700;
            font-family: 'Space Grotesk', sans-serif;
            color: var(--gold);
            min-width: 60px;
            text-align: center;
          }
          .helper-text {
            display: block;
            text-align: center;
            font-size: 12px;
            color: var(--text-muted);
            margin-top: 10px;
          }
          select {
            width: 100%;
            padding: 14px;
            background: var(--bg-surface-light);
            border: none;
            border-radius: 10px;
            color: var(--text-primary);
            font-size: 14px;
          }
          .settings-buttons {
            display: flex;
            gap: 12px;
            margin-top: 20px;
          }
          .settings-buttons .btn { flex: 1; padding: 16px; }
        `}</style>
      </div>
    );
  }

  // Dashboard View
  if (view === 'dashboard') {
    return (
      <div className="memorization-dashboard">
        {/* Stats Header */}
        <div className="stats-header">
          <div className="stat-item">
            <Flame size={20} className="flame" />
            <div>
              <span className="stat-value">{progress.streak}</span>
              <span className="stat-label">Day Streak</span>
            </div>
          </div>
          <div className="stat-item">
            <Star size={20} className="star" />
            <div>
              <span className="stat-value">{progress.points}</span>
              <span className="stat-label">Points</span>
            </div>
          </div>
          <div className="stat-item">
            <BookOpen size={20} className="book" />
            <div>
              <span className="stat-value">{progress.memorizedAyats.length}</span>
              <span className="stat-label">Memorized</span>
            </div>
          </div>
        </div>

        {/* Daily Goals */}
        <div className="daily-goals">
          <div className="goals-header">
            <h3>Today's Goals</h3>
            <button className="settings-btn" onClick={() => setView('settings')}>
              <Settings size={18} />
            </button>
          </div>

          <div className="goal-card" onClick={() => setView('memorize')}>
            <div className="goal-info">
              <span className="goal-emoji"></span>
              <div>
                <h4>New Memorization</h4>
                <span className="goal-progress">
                  {progress.todayNew} / {settings.dailyNewAyats} ayahs
                </span>
              </div>
            </div>
            <div className="goal-bar">
              <div className="goal-fill" style={{ width: `${dailyGoalProgress.new}%` }} />
            </div>
            {progress.todayNew < settings.dailyNewAyats ? (
              <ChevronRight size={20} className="chevron" />
            ) : (
              <Check size={20} className="check-icon" />
            )}
          </div>

          <div className="goal-card" onClick={() => setView('review')}>
            <div className="goal-info">
              <span className="goal-emoji"></span>
              <div>
                <h4>Review</h4>
                <span className="goal-progress">
                  {progress.todayReviewed} / {settings.dailyReviewAyats} ayahs
                  {reviewDueCount > 0 && <span className="due-badge">{reviewDueCount} due</span>}
                </span>
              </div>
            </div>
            <div className="goal-bar review">
              <div className="goal-fill" style={{ width: `${dailyGoalProgress.review}%` }} />
            </div>
            {progress.todayReviewed < settings.dailyReviewAyats ? (
              <ChevronRight size={20} className="chevron" />
            ) : (
              <Check size={20} className="check-icon" />
            )}
          </div>
        </div>

        {/* Goal Completion Banner */}
        {dailyGoalProgress.new >= 100 && dailyGoalProgress.review >= 100 && (
          <div className="completion-banner">
            <Award size={24} />
            <div>
              <h4>Daily Goal Complete!</h4>
              <p>+20 bonus points  Streak: {progress.streak} days</p>
            </div>
          </div>
        )}

        {/* Quick Actions */}
        <div className="quick-actions">
          <button className="action-btn primary" onClick={() => setView('memorize')}>
            <Plus size={18} />
            Memorize New
          </button>
          <button className="action-btn secondary" onClick={() => setView('review')}>
            <Calendar size={18} />
            Review ({reviewDueCount})
          </button>
        </div>

        <style>{`
          .memorization-dashboard { animation: fadeIn 0.3s ease-out; }
          .stats-header {
            display: flex;
            justify-content: space-between;
            background: linear-gradient(135deg, var(--primary) 0%, #1a4a3a 100%);
            padding: 20px;
            border-radius: 16px;
            margin-bottom: 20px;
          }
          .stat-item {
            display: flex;
            align-items: center;
            gap: 10px;
          }
          .stat-item .flame { color: #e74c3c; }
          .stat-item .star { color: var(--gold); }
          .stat-item .book { color: #3498db; }
          .stat-value {
            display: block;
            font-size: 20px;
            font-weight: 700;
            font-family: 'Space Grotesk', sans-serif;
          }
          .stat-label {
            font-size: 11px;
            color: var(--text-muted);
          }
          .daily-goals {
            background: var(--bg-surface);
            border-radius: 16px;
            padding: 20px;
            margin-bottom: 16px;
          }
          .goals-header {
            display: flex;
            justify-content: space-between;
            align-items: center;
            margin-bottom: 16px;
          }
          .goals-header h3 { font-size: 16px; }
          .settings-btn {
            background: var(--bg-surface-light);
            border: none;
            width: 36px;
            height: 36px;
            border-radius: 10px;
            color: var(--text-muted);
            cursor: pointer;
            display: flex;
            align-items: center;
            justify-content: center;
          }
          .goal-card {
            display: flex;
            align-items: center;
            gap: 12px;
            padding: 16px;
            background: var(--bg-surface-light);
            border-radius: 12px;
            margin-bottom: 10px;
            cursor: pointer;
            transition: all 0.2s ease;
          }
          .goal-card:hover { background: var(--bg-primary); }
          .goal-info {
            display: flex;
            align-items: center;
            gap: 12px;
            flex: 1;
          }
          .goal-emoji { font-size: 24px; }
          .goal-info h4 { font-size: 14px; margin-bottom: 2px; }
          .goal-progress { font-size: 12px; color: var(--text-muted); }
          .due-badge {
            background: var(--gold);
            color: var(--bg-primary);
            padding: 2px 8px;
            border-radius: 10px;
            font-size: 10px;
            font-weight: 600;
            margin-left: 8px;
          }
          .goal-bar {
            width: 60px;
            height: 6px;
            background: var(--bg-surface);
            border-radius: 3px;
            overflow: hidden;
          }
          .goal-fill {
            height: 100%;
            background: var(--gold);
            transition: width 0.3s ease;
          }
          .goal-bar.review .goal-fill { background: #3498db; }
          .chevron { color: var(--text-muted); }
          .check-icon { color: var(--success); }
          .completion-banner {
            display: flex;
            align-items: center;
            gap: 16px;
            background: linear-gradient(135deg, rgba(46,204,113,0.2) 0%, rgba(46,204,113,0.05) 100%);
            border: 1px solid rgba(46,204,113,0.3);
            padding: 16px;
            border-radius: 14px;
            margin-bottom: 16px;
          }
          .completion-banner svg { color: var(--success); }
          .completion-banner h4 { font-size: 14px; color: var(--success); }
          .completion-banner p { font-size: 12px; color: var(--text-muted); }
          .quick-actions {
            display: grid;
            grid-template-columns: 1fr 1fr;
            gap: 12px;
          }
          .action-btn {
            display: flex;
            align-items: center;
            justify-content: center;
            gap: 8px;
            padding: 16px;
            border: none;
            border-radius: 12px;
            font-size: 14px;
            font-weight: 600;
            cursor: pointer;
          }
          .action-btn.primary {
            background: var(--gold);
            color: var(--bg-primary);
          }
          .action-btn.secondary {
            background: var(--bg-surface);
            color: var(--text-primary);
          }
          .loading-state {
            display: flex;
            flex-direction: column;
            align-items: center;
            justify-content: center;
            padding: 60px;
          }
          .spinner {
            width: 40px;
            height: 40px;
            border: 3px solid var(--bg-surface-light);
            border-top-color: var(--gold);
            border-radius: 50%;
            animation: spin 1s linear infinite;
            margin-bottom: 16px;
          }
          @keyframes spin { to { transform: rotate(360deg); } }
        `}</style>
      </div>
    );
  }

  // Memorize View
  if (view === 'memorize') {
    const currentAyah = todayAyahs[currentAyahIndex];

    return (
      <div className="memorize-view">
        <button className="back-btn" onClick={() => setView('dashboard')}>
          <ChevronLeft size={18} /> Back
        </button>

        <div className="progress-header">
          <span>Ayah {currentAyahIndex + 1} of {todayAyahs.length}</span>
          <div className="progress-dots">
            {todayAyahs.map((_, i) => (
              <span 
                key={i} 
                className={`dot ${i < currentAyahIndex ? 'done' : i === currentAyahIndex ? 'current' : ''}`}
              />
            ))}
          </div>
        </div>

        {currentAyah ? (
          <div className="ayah-card">
            <div className="ayah-header">
              <span>{currentAyah.surahName}</span>
              <span>Ayah {currentAyah.numberInSurah}</span>
            </div>
            <p className="arabic ayah-text">{currentAyah.text}</p>
          </div>
        ) : (
          <div className="loading-ayahs">Loading ayahs...</div>
        )}

        <div className="memorize-actions">
          <button 
            className="btn btn-secondary"
            onClick={() => setCurrentAyahIndex(Math.max(0, currentAyahIndex - 1))}
            disabled={currentAyahIndex === 0}
          >
            <ChevronLeft size={18} /> Previous
          </button>

          <button 
            className="btn btn-primary"
            onClick={() => {
              if (currentAyah) {
                markAyahMemorized(settings.currentSurah, currentAyah.numberInSurah, currentAyah.text);
              }
              if (currentAyahIndex < todayAyahs.length - 1) {
                setCurrentAyahIndex(currentAyahIndex + 1);
              } else {
                setView('dashboard');
              }
            }}
          >
            <Check size={18} />
            {currentAyahIndex < todayAyahs.length - 1 ? 'Memorized' : 'Complete'}
          </button>
        </div>

        <style>{`
          .memorize-view { animation: fadeIn 0.3s ease-out; }
          .back-btn {
            display: flex;
            align-items: center;
            gap: 4px;
            background: none;
            border: none;
            color: var(--gold);
            font-size: 14px;
            cursor: pointer;
            margin-bottom: 16px;
          }
          .progress-header {
            display: flex;
            justify-content: space-between;
            align-items: center;
            margin-bottom: 20px;
          }
          .progress-header span { font-size: 13px; color: var(--text-muted); }
          .progress-dots { display: flex; gap: 6px; }
          .dot {
            width: 8px;
            height: 8px;
            border-radius: 50%;
            background: var(--bg-surface-light);
          }
          .dot.done { background: var(--success); }
          .dot.current { background: var(--gold); }
          .ayah-card {
            background: var(--bg-surface);
            border-radius: 20px;
            padding: 24px;
            margin-bottom: 20px;
          }
          .ayah-header {
            display: flex;
            justify-content: space-between;
            font-size: 13px;
            color: var(--text-muted);
            margin-bottom: 16px;
          }
          .ayah-text {
            font-size: 28px;
            line-height: 2.2;
            text-align: center;
          }
          .memorize-actions {
            display: flex;
            gap: 12px;
          }
          .memorize-actions .btn { flex: 1; padding: 16px; }
          .loading-ayahs {
            text-align: center;
            padding: 60px;
            color: var(--text-muted);
          }
        `}</style>
      </div>
    );
  }

  // Review View
  if (view === 'review') {
    const currentReview = reviewAyahs[currentAyahIndex];

    return (
      <div className="review-view">
        <button className="back-btn" onClick={() => { setView('dashboard'); setCurrentAyahIndex(0); }}>
          <ChevronLeft size={18} /> Back
        </button>

        {reviewAyahs.length === 0 ? (
          <div className="no-reviews">
            <Check size={48} />
            <h3>No Reviews Due!</h3>
            <p>Great job! Check back tomorrow.</p>
          </div>
        ) : currentReview ? (
          <>
            <div className="progress-header">
              <span>Review {currentAyahIndex + 1} of {reviewAyahs.length}</span>
            </div>

            <div className="review-card">
              <p className="arabic review-text">{currentReview.text}...</p>
              <span className="review-ref">{currentReview.surah}:{currentReview.ayahNum}</span>
            </div>

            <p className="review-prompt">Did you remember this correctly?</p>

            <div className="review-actions">
              <button 
                className="btn btn-error"
                onClick={() => {
                  markAyahReviewed(currentReview.id, false);
                  if (currentAyahIndex < reviewAyahs.length - 1) {
                    setCurrentAyahIndex(currentAyahIndex + 1);
                  } else {
                    setView('dashboard');
                    setCurrentAyahIndex(0);
                  }
                }}
              >
                <X size={18} /> Struggled
              </button>

              <button 
                className="btn btn-success"
                onClick={() => {
                  markAyahReviewed(currentReview.id, true);
                  if (currentAyahIndex < reviewAyahs.length - 1) {
                    setCurrentAyahIndex(currentAyahIndex + 1);
                  } else {
                    setView('dashboard');
                    setCurrentAyahIndex(0);
                  }
                }}
              >
                <Check size={18} /> Got It!
              </button>
            </div>
          </>
        ) : null}

        <style>{`
          .review-view { animation: fadeIn 0.3s ease-out; }
          .no-reviews {
            text-align: center;
            padding: 60px 20px;
          }
          .no-reviews svg { color: var(--success); margin-bottom: 16px; }
          .no-reviews h3 { margin-bottom: 8px; }
          .no-reviews p { color: var(--text-muted); }
          .review-card {
            background: var(--bg-surface);
            border-radius: 20px;
            padding: 30px;
            text-align: center;
            margin-bottom: 20px;
          }
          .review-text {
            font-size: 26px;
            line-height: 2;
            margin-bottom: 16px;
          }
          .review-ref {
            font-size: 12px;
            color: var(--gold);
            background: var(--primary);
            padding: 4px 12px;
            border-radius: 10px;
          }
          .review-prompt {
            text-align: center;
            color: var(--text-secondary);
            margin-bottom: 16px;
          }
          .review-actions {
            display: grid;
            grid-template-columns: 1fr 1fr;
            gap: 12px;
          }
          .btn-error {
            background: rgba(231,76,60,0.2);
            color: var(--error);
            border: none;
            padding: 16px;
            border-radius: 12px;
            font-weight: 600;
            cursor: pointer;
            display: flex;
            align-items: center;
            justify-content: center;
            gap: 8px;
          }
          .btn-success {
            background: rgba(46,204,113,0.2);
            color: var(--success);
            border: none;
            padding: 16px;
            border-radius: 12px;
            font-weight: 600;
            cursor: pointer;
            display: flex;
            align-items: center;
            justify-content: center;
            gap: 8px;
          }
        `}</style>
      </div>
    );
  }

  return null;
}

export default MemorizationPlan;
