import React, { createContext, useContext, useState, useEffect } from 'react';
import { 
  createUserWithEmailAndPassword,
  signInWithEmailAndPassword,
  signOut,
  onAuthStateChanged,
  updateProfile,
  GoogleAuthProvider,
  signInWithPopup
} from 'firebase/auth';
import { doc, setDoc, getDoc, updateDoc } from 'firebase/firestore';
import { auth, db } from '../config/firebase';

const AuthContext = createContext();

// Subscription tiers
export const TIERS = {
  FREE: 'free',
  PRO: 'pro',
  PRO_PLUS: 'pro_plus'
};

export const TIER_LABELS = {
  [TIERS.FREE]: 'Free',
  [TIERS.PRO]: 'Pro',
  [TIERS.PRO_PLUS]: 'Pro+'
};

export const TIER_COLORS = {
  [TIERS.FREE]: '#6b7280',
  [TIERS.PRO]: '#d4af37',
  [TIERS.PRO_PLUS]: '#8b5cf6'
};

// Feature access by tier
export const FEATURES = {
  salahTracking: [TIERS.PRO, TIERS.PRO_PLUS],
  salahHistory: [TIERS.PRO, TIERS.PRO_PLUS],
  fullQuran: [TIERS.PRO, TIERS.PRO_PLUS],
  memorization: [TIERS.PRO, TIERS.PRO_PLUS],
  revision: [TIERS.PRO, TIERS.PRO_PLUS],
  musabaqah: [TIERS.PRO, TIERS.PRO_PLUS],
  tajweed: [TIERS.PRO, TIERS.PRO_PLUS],
  unlimitedHabits: [TIERS.PRO, TIERS.PRO_PLUS],
  workout: [TIERS.PRO, TIERS.PRO_PLUS],
  customWorkoutPdf: [TIERS.PRO_PLUS],
  weightTracking: [TIERS.PRO_PLUS],
};

export function AuthProvider({ children }) {
  const [user, setUser] = useState(null);
  const [userProfile, setUserProfile] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // Create user profile in Firestore
  const createUserProfile = async (uid, data) => {
    const userRef = doc(db, 'users', uid);
    const defaultProfile = {
      displayName: data.displayName || '',
      email: data.email || '',
      tier: TIERS.FREE,
      createdAt: new Date().toISOString(),
      xp: 0,
      level: 1,
      streak: 0,
      customWorkoutPdf: null,
      ...data
    };
    await setDoc(userRef, defaultProfile);
    return defaultProfile;
  };

  // Get user profile from Firestore
  const getUserProfile = async (uid) => {
    const userRef = doc(db, 'users', uid);
    const docSnap = await getDoc(userRef);
    if (docSnap.exists()) {
      return docSnap.data();
    }
    return null;
  };

  // Sign up with email/password
  const signup = async (email, password, displayName) => {
    try {
      setError(null);
      const result = await createUserWithEmailAndPassword(auth, email, password);
      await updateProfile(result.user, { displayName });
      const profile = await createUserProfile(result.user.uid, { 
        displayName, 
        email 
      });
      setUserProfile(profile);
      return result.user;
    } catch (err) {
      setError(err.message);
      throw err;
    }
  };

  // Login with email/password
  const login = async (email, password) => {
    try {
      setError(null);
      const result = await signInWithEmailAndPassword(auth, email, password);
      const profile = await getUserProfile(result.user.uid);
      setUserProfile(profile);
      return result.user;
    } catch (err) {
      setError(err.message);
      throw err;
    }
  };

  // Login with Google
  const loginWithGoogle = async () => {
    try {
      setError(null);
      const provider = new GoogleAuthProvider();
      const result = await signInWithPopup(auth, provider);

      // Check if user profile exists, if not create one
      let profile = await getUserProfile(result.user.uid);
      if (!profile) {
        profile = await createUserProfile(result.user.uid, {
          displayName: result.user.displayName,
          email: result.user.email
        });
      }
      setUserProfile(profile);
      return result.user;
    } catch (err) {
      setError(err.message);
      throw err;
    }
  };

  // Logout
  const logout = async () => {
    try {
      await signOut(auth);
      setUserProfile(null);
    } catch (err) {
      setError(err.message);
      throw err;
    }
  };

  // Update user tier (for payment integration)
  const updateTier = async (newTier) => {
    if (!user) return;
    const userRef = doc(db, 'users', user.uid);
    await updateDoc(userRef, { tier: newTier });
    setUserProfile(prev => ({ ...prev, tier: newTier }));
  };

  // Update user profile
  const updateUserProfile = async (data) => {
    if (!user) return;
    const userRef = doc(db, 'users', user.uid);
    await updateDoc(userRef, data);
    setUserProfile(prev => ({ ...prev, ...data }));
  };

  // Check if user has access to a feature
  const hasAccess = (feature) => {
    if (!userProfile) return false;
    const allowedTiers = FEATURES[feature];
    if (!allowedTiers) return true; // Feature not restricted
    return allowedTiers.includes(userProfile.tier);
  };

  // Get user's tier
  const getTier = () => {
    return userProfile?.tier || TIERS.FREE;
  };

  // Listen for auth state changes
  useEffect(() => {
    const unsubscribe = onAuthStateChanged(auth, async (firebaseUser) => {
      setUser(firebaseUser);
      if (firebaseUser) {
        const profile = await getUserProfile(firebaseUser.uid);
        setUserProfile(profile);
      } else {
        setUserProfile(null);
      }
      setLoading(false);
    });

    return unsubscribe;
  }, []);

  const value = {
    user,
    userProfile,
    loading,
    error,
    signup,
    login,
    loginWithGoogle,
    logout,
    updateTier,
    updateUserProfile,
    hasAccess,
    getTier,
    TIERS,
    TIER_LABELS,
    TIER_COLORS
  };

  return (
    <AuthContext.Provider value={value}>
      {children}
    </AuthContext.Provider>
  );
}

export function useAuth() {
  const context = useContext(AuthContext);
  if (!context) {
    throw new Error('useAuth must be used within an AuthProvider');
  }
  return context;
}
