import React, { useState, useEffect } from 'react';
import { useApp } from '../context/AppContext';
import { format } from 'date-fns';
import { Check, X, Clock, MapPin } from 'lucide-react';
import axios from 'axios';

const PRAYER_NAMES = ['fajr', 'dhuhr', 'asr', 'maghrib', 'isha'];
const PRAYER_LABELS = {
  fajr: 'Fajr',
  dhuhr: 'Dhuhr',
  asr: 'Asr',
  maghrib: 'Maghrib',
  isha: 'Isha'
};
const PRAYER_ICONS = {
  fajr: 'ðŸŒ…',
  dhuhr: 'â˜€ï¸',
  asr: 'ðŸŒ¤ï¸',
  maghrib: 'ðŸŒ†',
  isha: 'ðŸŒ™'
};

function Salah() {
  const { prayers, markPrayer, location, xp } = useApp();
  const [prayerTimes, setPrayerTimes] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    if (!location) return;

    axios.get(`http://localhost:5000/api/prayer-times?latitude=${location.latitude}&longitude=${location.longitude}`)
      .then(res => {
        setPrayerTimes(res.data);
        setLoading(false);
      })
      .catch(err => {
        console.error(err);
        setLoading(false);
      });
  }, [location]);

  const getStatusIcon = (status) => {
    if (status === 'on_time') return <Check className="status-icon on-time" size={20} />;
    if (status === 'late') return <Clock className="status-icon late" size={20} />;
    if (status === 'missed') return <X className="status-icon missed" size={20} />;
    return null;
  };

  const isPrayerPast = (prayerName) => {
    if (!prayerTimes) return false;
    const time = new Date(prayerTimes[prayerName]);
    return time < new Date();
  };

  return (
    <div className="salah-page">
      <header className="page-header">
        <h1>Salah</h1>
        <p className="subtitle">{format(new Date(), 'EEEE, MMMM d, yyyy')}</p>
      </header>

      {location && (
        <div className="location-badge">
          <MapPin size={14} />
          <span>Location detected</span>
        </div>
      )}

      <div className="prayers-list">
        {PRAYER_NAMES.map((prayer) => {
          const time = prayerTimes ? new Date(prayerTimes[prayer]) : null;
          const status = prayers[prayer];
          const isPast = isPrayerPast(prayer);

          return (
            <div 
              key={prayer} 
              className={`prayer-card ${status ? 'completed' : ''} ${isPast && !status ? 'past' : ''}`}
            >
              <div className="prayer-main">
                <span className="prayer-emoji">{PRAYER_ICONS[prayer]}</span>
                <div className="prayer-info">
                  <h3>{PRAYER_LABELS[prayer]}</h3>
                  <span className="prayer-time">
                    {time ? format(time, 'h:mm a') : '--:--'}
                  </span>
                </div>
                {getStatusIcon(status)}
              </div>

              {!status && isPast && (
                <div className="prayer-actions">
                  <button 
                    className="action-btn on-time"
                    onClick={() => markPrayer(prayer, 'on_time')}
                  >
                    <Check size={16} /> On Time
                  </button>
                  <button 
                    className="action-btn late"
                    onClick={() => markPrayer(prayer, 'late')}
                  >
                    <Clock size={16} /> Late
                  </button>
                  <button 
                    className="action-btn missed"
                    onClick={() => markPrayer(prayer, 'missed')}
                  >
                    <X size={16} /> Missed
                  </button>
                </div>
              )}
            </div>
          );
        })}
      </div>

      <div className="xp-earned">
        <span>XP Earned Today: </span>
        <strong>{Object.values(prayers).reduce((acc, status) => {
          if (status === 'on_time') return acc + 50;
          if (status === 'late') return acc + 20;
          return acc;
        }, 0)}</strong>
      </div>

      <style>{`
        .salah-page {
          padding: 20px;
          padding-bottom: 100px;
        }
        .page-header {
          margin-bottom: 20px;
        }
        .page-header h1 {
          font-size: 28px;
          margin-bottom: 4px;
        }
        .subtitle {
          color: var(--text-muted);
          font-size: 14px;
        }
        .location-badge {
          display: inline-flex;
          align-items: center;
          gap: 6px;
          background: var(--bg-surface);
          padding: 8px 12px;
          border-radius: 20px;
          font-size: 12px;
          color: var(--success);
          margin-bottom: 20px;
        }
        .prayers-list {
          display: flex;
          flex-direction: column;
          gap: 12px;
        }
        .prayer-card {
          background: var(--bg-surface);
          border-radius: 16px;
          padding: 16px;
          transition: all 0.2s ease;
        }
        .prayer-card.completed {
          border-left: 4px solid var(--success);
        }
        .prayer-card.past:not(.completed) {
          border-left: 4px solid var(--warning);
        }
        .prayer-main {
          display: flex;
          align-items: center;
          gap: 12px;
        }
        .prayer-emoji {
          font-size: 28px;
        }
        .prayer-info {
          flex: 1;
        }
        .prayer-info h3 {
          font-size: 18px;
          margin-bottom: 4px;
        }
        .prayer-time {
          color: var(--text-muted);
          font-size: 14px;
        }
        .status-icon {
          padding: 8px;
          border-radius: 50%;
        }
        .status-icon.on-time {
          color: var(--success);
          background: rgba(46, 204, 113, 0.2);
        }
        .status-icon.late {
          color: var(--warning);
          background: rgba(241, 196, 15, 0.2);
        }
        .status-icon.missed {
          color: var(--error);
          background: rgba(231, 76, 60, 0.2);
        }
        .prayer-actions {
          display: flex;
          gap: 8px;
          margin-top: 12px;
          padding-top: 12px;
          border-top: 1px solid var(--bg-surface-light);
        }
        .action-btn {
          flex: 1;
          display: flex;
          align-items: center;
          justify-content: center;
          gap: 6px;
          padding: 10px;
          border-radius: 10px;
          border: none;
          font-size: 12px;
          font-weight: 600;
          cursor: pointer;
          transition: all 0.2s ease;
        }
        .action-btn.on-time {
          background: rgba(46, 204, 113, 0.2);
          color: var(--success);
        }
        .action-btn.on-time:hover {
          background: var(--success);
          color: white;
        }
        .action-btn.late {
          background: rgba(241, 196, 15, 0.2);
          color: var(--warning);
        }
        .action-btn.late:hover {
          background: var(--warning);
          color: var(--bg-primary);
        }
        .action-btn.missed {
          background: rgba(231, 76, 60, 0.2);
          color: var(--error);
        }
        .action-btn.missed:hover {
          background: var(--error);
          color: white;
        }
        .xp-earned {
          text-align: center;
          margin-top: 24px;
          padding: 16px;
          background: var(--bg-surface);
          border-radius: 12px;
          color: var(--text-secondary);
        }
        .xp-earned strong {
          color: var(--gold);
          font-size: 20px;
          margin-left: 8px;
        }
      `}</style>
    </div>
  );
}

export default Salah;
