  114: { juz: 30, name: 'An-Nas', arabicName: 'Ø§Ù„Ù†Ø§Ø³', ayahs: 6 },
};

// Question Types for Hard Musabaqah
const QUESTION_TYPES = [
  'continue_ayah',      // Continue from this ayah (hardest)
  'complete_ayah',      // Fill in the missing word(s)
  'identify_surah',     // Which surah is this from?
  'next_ayah',          // What comes after this ayah?
  'previous_ayah',      // What comes before this ayah?
  'identify_juz',       // Which Juz is this from?
  'ayah_number',        // What is the ayah number?
];

function QuranQuiz() {
  const { user } = useAuth();

  const [mode, setMode] = useState('setup'); // setup, quiz, results
  const [quizConfig, setQuizConfig] = useState({
    numRounds: 10,
    difficulty: 'hard', // hard, expert, master
    recitationLength: 1, // 1 or 1.5 pages worth
  });

  const [questions, setQuestions] = useState([]);
  const [currentQuestion, setCurrentQuestion] = useState(0);
  const [userAnswers, setUserAnswers] = useState([]);
  const [showAnswer, setShowAnswer] = useState(false);
  const [mistakes, setMistakes] = useState([]);
  const [score, setScore] = useState({ correct: 0, total: 0 });
  const [loading, setLoading] = useState(false);
  const [quizHistory, setQuizHistory] = useState([]);

  // Load quiz history
  useEffect(() => {
    if (user) {
      loadQuizHistory();
    }
  }, [user]);

  const loadQuizHistory = async () => {
    try {
      const docRef = doc(db, 'users', user.uid, 'quran', 'quiz');
      const docSnap = await getDoc(docRef);
      if (docSnap.exists()) {
        setQuizHistory(docSnap.data().history || []);
      }
    } catch (error) {
      console.error('Error loading quiz history:', error);
    }
  };

  const saveQuizResult = async (result) => {
    try {
      const docRef = doc(db, 'users', user.uid, 'quran', 'quiz');
      const newHistory = [...quizHistory, result].slice(-50); // Keep last 50
      await setDoc(docRef, { history: newHistory, updatedAt: new Date().toISOString() });
      setQuizHistory(newHistory);
    } catch (error) {
      console.error('Error saving quiz result:', error);
    }
  };

  // Generate random questions from ENTIRE Quran (Juz 1-30)
  const generateQuestions = async () => {
    setLoading(true);
    const generatedQuestions = [];
    const usedAyahs = new Set(); // Prevent duplicate questions

    // Select random surahs from across all 30 Juz
    const allSurahs = Object.keys(SURAH_JUZ_MAP).map(Number);

    for (let i = 0; i < quizConfig.numRounds; i++) {
      // Pick a random surah ensuring coverage across Juz
      const targetJuz = Math.floor(Math.random() * 30) + 1;

      // Find surahs in or near this Juz
      const surahsInJuz = allSurahs.filter(s => {
        const juz = SURAH_JUZ_MAP[s].juz;
        if (Array.isArray(juz)) {
          return juz.includes(targetJuz) || juz.some(j => Math.abs(j - targetJuz) <= 2);
        }
        return juz === targetJuz || Math.abs(juz - targetJuz) <= 2;
      });

      const randomSurah = surahsInJuz[Math.floor(Math.random() * surahsInJuz.length)];
      const surahInfo = SURAH_JUZ_MAP[randomSurah];

      // Pick random ayah from this surah
      let randomAyah;
      let attempts = 0;
      do {
        randomAyah = Math.floor(Math.random() * (surahInfo.ayahs - 2)) + 1;
        attempts++;
      } while (usedAyahs.has(`${randomSurah}:${randomAyah}`) && attempts < 10);

      usedAyahs.add(`${randomSurah}:${randomAyah}`);

      // Fetch the ayah and surrounding context
      try {
        const response = await fetch(
          `https://api.alquran.cloud/v1/ayah/${randomSurah}:${randomAyah}/ar.alafasy`
        );
        const data = await response.json();

        if (data.code === 200) {
          // Fetch next ayah for continuation questions
          const nextResponse = await fetch(
            `https://api.alquran.cloud/v1/ayah/${randomSurah}:${randomAyah + 1}/ar.alafasy`
          );
          const nextData = await nextResponse.json();

          // Select random question type
          const questionType = QUESTION_TYPES[Math.floor(Math.random() * QUESTION_TYPES.length)];

          generatedQuestions.push({
            id: i,
            type: questionType,
            surah: randomSurah,
            surahName: surahInfo.name,
            surahArabic: surahInfo.arabicName,
            juz: Array.isArray(surahInfo.juz) ? surahInfo.juz[0] : surahInfo.juz,
            ayahNum: randomAyah,
            ayahText: data.data.text,
            nextAyahText: nextData.code === 200 ? nextData.data.text : null,
            audio: data.data.audio,
          });
        }
      } catch (error) {
        console.error('Error fetching ayah:', error);
      }
    }

    // Shuffle questions for randomness
    generatedQuestions.sort(() => Math.random() - 0.5);

    setQuestions(generatedQuestions);
    setCurrentQuestion(0);
    setUserAnswers([]);
    setMistakes([]);
    setScore({ correct: 0, total: generatedQuestions.length });
    setMode('quiz');
    setLoading(false);
  };

  const handleAnswer = (isCorrect, mistakeText = null) => {
    const newAnswers = [...userAnswers, { 
      questionId: currentQuestion, 
      correct: isCorrect,
      mistake: mistakeText
    }];
    setUserAnswers(newAnswers);

    if (mistakeText) {
      setMistakes([...mistakes, { questionId: currentQuestion, text: mistakeText }]);
    }

    if (isCorrect) {
      setScore(prev => ({ ...prev, correct: prev.correct + 1 }));
    }

    setShowAnswer(false);

    if (currentQuestion < questions.length - 1) {
      setCurrentQuestion(currentQuestion + 1);
    } else {
      // Quiz complete
      const finalScore = {
        correct: isCorrect ? score.correct + 1 : score.correct,
        total: questions.length,
        percentage: Math.round(((isCorrect ? score.correct + 1 : score.correct) / questions.length) * 100),
        date: new Date().toISOString(),
        difficulty: quizConfig.difficulty,
        mistakes: [...mistakes, mistakeText ? { questionId: currentQuestion, text: mistakeText } : null].filter(Boolean)
      };
      saveQuizResult(finalScore);
      setMode('results');
    }
  };

  const getQuestionPrompt = (question) => {
    switch (question.type) {
      case 'continue_ayah':
        return 'Continue reciting from this ayah (1-1.5 pages):';
      case 'complete_ayah':
        return 'Complete this ayah:';
      case 'identify_surah':
        return 'Which Surah is this ayah from?';
      case 'next_ayah':
        return 'Recite the ayah that comes AFTER this:';
      case 'previous_ayah':
        return 'Recite the ayah that comes BEFORE this:';
      case 'identify_juz':
        return 'Which Juz is this ayah from?';
      case 'ayah_number':
        return 'What is the ayah number of this verse?';
      default:
        return 'Continue from this ayah:';
    }
  };

  const getScoreMessage = (percentage) => {
    if (percentage >= 90) return { emoji: 'ðŸ†', message: 'EXCELLENT! You are a Hafiz!', color: 'var(--gold)' };
    if (percentage >= 70) return { emoji: 'â­', message: 'Great job! Keep practicing!', color: 'var(--success)' };
    if (percentage >= 50) return { emoji: 'ðŸ“–', message: 'Good effort. Review and try again.', color: 'var(--warning)' };
    return { emoji: 'ðŸ’ª', message: 'Keep memorizing and practicing!', color: 'var(--error)' };
  };

  // Setup Screen
  if (mode === 'setup') {
    return (
