            color: var(--gold);
          }
          .difficulty-selector {
            display: flex;
            flex-direction: column;
            gap: 10px;
          }
          .diff-btn {
            display: flex;
            flex-direction: column;
            align-items: flex-start;
            padding: 14px;
            background: var(--bg-surface-light);
            border: 2px solid transparent;
            border-radius: 12px;
            cursor: pointer;
            transition: all 0.2s ease;
            text-align: left;
          }
          .diff-btn.active {
            background: var(--primary);
            border-color: var(--gold);
          }
          .diff-label {
            font-size: 14px;
            font-weight: 600;
            color: var(--text-primary);
          }
          .diff-desc {
