          }
          .quiz-info svg { flex-shrink: 0; margin-top: 2px; }
          .start-quiz-btn {
            width: 100%;
            display: flex;
            align-items: center;
            justify-content: center;
            gap: 10px;
            padding: 18px;
            background: var(--gold);
            border: none;
            border-radius: 14px;
            color: var(--bg-primary);
            font-size: 16px;
            font-weight: 700;
            cursor: pointer;
            transition: all 0.2s ease;
          }
          .start-quiz-btn:hover:not(:disabled) {
            transform: scale(1.02);
          }
          .start-quiz-btn:disabled {
            opacity: 0.7;
            cursor: not-allowed;
          }
          .quiz-history {
            margin-top: 24px;
            background: var(--bg-surface);
            padding: 16px;
            border-radius: 14px;
          }
          .quiz-history h3 {
            font-size: 14px;
            color: var(--text-secondary);
            margin-bottom: 12px;
          }
          .history-list { display: flex; flex-direction: column; gap: 8px; }
          .history-item {
            display: flex;
            align-items: center;
            gap: 12px;
            padding: 10px 12px;
            background: var(--bg-surface-light);
            border-radius: 10px;
          }
          .history-score {
            font-size: 16px;
            font-weight: 700;
            color: var(--gold);
            min-width: 50px;
          }
          .history-detail { font-size: 13px; color: var(--text-muted); flex: 1; }
          .history-date { font-size: 11px; color: var(--text-muted); }
          @keyframes fadeIn { from { opacity: 0; } to { opacity: 1; } }
        `}</style>
      </div>
    );
  }

  // Quiz Screen
  if (mode === 'quiz' && questions.length > 0) {
    const question = questions[currentQuestion];

    return (
      <div className="quiz-active">
        <div className="quiz-progress">
          <span>Question {currentQuestion + 1} / {questions.length}</span>
          <div className="progress-bar">
            <div className="progress-fill" style={{ width: `${((currentQuestion + 1) / questions.length) * 100}%` }} />
          </div>
          <span className="score-display">{score.correct} correct</span>
        </div>

        <div className="question-card">
          <div className="question-meta">
            <span className="juz-badge">Juz {question.juz}</span>
            <span className="surah-badge">{question.surahName}</span>
          </div>

          <p className="question-prompt">{getQuestionPrompt(question)}</p>

          <div className="ayah-display arabic">
            {question.ayahText}
          </div>

          <span className="ayah-ref">{question.surahArabic} - Ayah {question.ayahNum}</span>
        </div>

        {/* Show/Hide Answer */}
        <button 
          className="reveal-btn"
          onClick={() => setShowAnswer(!showAnswer)}
        >
          {showAnswer ? <EyeOff size={18} /> : <Eye size={18} />}
          {showAnswer ? 'Hide Answer' : 'Reveal Answer'}
        </button>

        {showAnswer && question.nextAyahText && (
          <div className="answer-card">
            <h4>Continuation:</h4>
            <p className="arabic answer-text">{question.nextAyahText}</p>
          </div>
        )}

        {/* Answer Buttons */}
        <div className="answer-buttons">
          <button className="answer-btn wrong" onClick={() => handleAnswer(false, question.ayahText)}>
            <X size={18} />
            Made Mistakes
          </button>
          <button className="answer-btn correct" onClick={() => handleAnswer(true)}>
            <Check size={18} />
