        children: `
          .memorization-dashboard { animation: fadeIn 0.3s ease-out; }

          .quran-progress-card {
            background: linear-gradient(135deg, var(--primary) 0%, #1a4a3a 100%);
            padding: 24px;
            border-radius: 20px;
            margin-bottom: 16px;
          }
          .progress-header-row {
            display: flex;
            align-items: center;
            gap: 10px;
            margin-bottom: 16px;
          }
          .progress-header-row svg { color: var(--gold); }
          .progress-header-row h3 { font-size: 16px; }
          .big-progress-bar {
            height: 14px;
            background: rgba(0,0,0,0.3);
            border-radius: 7px;
            overflow: hidden;
            margin-bottom: 16px;
          }
          .big-progress-bar .progress-fill {
            height: 100%;
            background: linear-gradient(90deg, var(--gold), #27ae60);
            border-radius: 7px;
            transition: width 0.5s ease;
          }
          .progress-stats-row {
            display: flex;
            justify-content: space-between;
            margin-bottom: 12px;
          }
          .progress-stat { text-align: center; }
          .progress-stat .stat-value {
            display: block;
            font-size: 20px;
            font-weight: 700;
            font-family: 'Space Grotesk', sans-serif;
          }
          .progress-stat .stat-label {
            font-size: 11px;
            color: var(--text-secondary);
          }
          .percentage-display {
            text-align: center;
            padding-top: 12px;
            border-top: 1px solid rgba(255,255,255,0.1);
          }
          .percentage {
            font-size: 32px;
            font-weight: 700;
            color: var(--gold);
            font-family: 'Space Grotesk', sans-serif;
          }
          .of-quran {
            display: block;
            font-size: 12px;
            color: var(--text-muted);
          }

          .stats-header {
            display: flex;
            justify-content: space-between;
            background: var(--bg-surface);
            padding: 16px;
            border-radius: 14px;
            margin-bottom: 16px;
          }
          .stat-item {
            display: flex;
            align-items: center;
            gap: 10px;
          }
          .stat-item .flame { color: #e74c3c; }
          .stat-item .star { color: var(--gold); }
          .stat-item .trophy { color: #3498db; }
          .stat-item .stat-value {
            display: block;
            font-size: 18px;
            font-weight: 700;
          }
          .stat-item .stat-label {
            font-size: 10px;
            color: var(--text-muted);
          }

          .daily-goals {
            background: var(--bg-surface);
            border-radius: 16px;
            padding: 20px;
            margin-bottom: 16px;
          }
          .goals-header {
            display: flex;
            justify-content: space-between;
            align-items: center;
            margin-bottom: 16px;
          }
          .goals-header h3 { font-size: 16px; }
          .settings-btn {
            background: var(--bg-surface-light);
            border: none;
            width: 36px;
            height: 36px;
            border-radius: 10px;
            color: var(--text-muted);
            cursor: pointer;
            display: flex;
            align-items: center;
            justify-content: center;
          }
          .goal-card {
            display: flex;
            align-items: center;
            gap: 12px;
            padding: 16px;
            background: var(--bg-surface-light);
            border-radius: 12px;
            margin-bottom: 10px;
            cursor: pointer;
            transition: all 0.2s ease;
          }
          .goal-card:hover { background: var(--bg-primary); }
          .goal-info {
            display: flex;
            align-items: center;
            gap: 12px;
            flex: 1;
          }
          .goal-emoji { font-size: 24px; }
          .goal-info h4 { font-size: 14px; margin-bottom: 2px; }
          .goal-progress { font-size: 12px; color: var(--text-muted); }
          .due-badge {
            background: var(--gold);
            color: var(--bg-primary);
            padding: 2px 8px;
            border-radius: 10px;
            font-size: 10px;
            font-weight: 600;
            margin-left: 8px;
          }
          .goal-bar {
            width: 60px;
            height: 6px;
            background: var(--bg-surface);
            border-radius: 3px;
            overflow: hidden;
          }
          .goal-fill {
            height: 100%;
            background: var(--gold);
            transition: width 0.3s ease;
          }
          .goal-bar.review .goal-fill { background: #3498db; }
          .chevron { color: var(--text-muted); }
          .check-icon { color: var(--success); }

          .completion-banner {
            display: flex;
            align-items: center;
            gap: 16px;
            background: linear-gradient(135deg, rgba(46,204,113,0.2) 0%, rgba(46,204,113,0.05) 100%);
            border: 1px solid rgba(46,204,113,0.3);
            padding: 16px;
            border-radius: 14px;
            margin-bottom: 16px;
          }
          .completion-banner svg { color: var(--success); }
          .completion-banner h4 { font-size: 14px; color: var(--success); }
          .completion-banner p { font-size: 12px; color: var(--text-muted); }

          .quick-actions {
            display: grid;
            grid-template-columns: 1fr 1fr;
            gap: 12px;
            margin-bottom: 16px;
          }
          .action-btn {
            display: flex;
            align-items: center;
            justify-content: center;
            gap: 8px;
            padding: 16px;
            border: none;
            border-radius: 12px;
            font-size: 13px;
            font-weight: 600;
            cursor: pointer;
          }
          .action-btn.primary {
            background: var(--gold);
            color: var(--bg-primary);
          }
          .action-btn.secondary {
            background: var(--bg-surface);
            color: var(--text-primary);
          }

          .juz-progress-section {
            background: var(--bg-surface);
            padding: 16px;
            border-radius: 14px;
          }
          .juz-progress-section h3 {
            font-size: 14px;
            color: var(--text-secondary);
            margin-bottom: 12px;
          }
          .juz-grid {
            display: grid;
