import React, { useState } from 'react';
import { useApp } from '../context/AppContext';
import { HelpCircle, Play, Check, X, Award, RotateCcw } from 'lucide-react';
import { SURAHS } from './QuranReader';

// Juz to Surah mapping
const JUZ_SURAHS = {
  1: [1, 2],
  2: [2],
  3: [2, 3],
  4: [3, 4],
  5: [4],
  6: [4, 5],
  7: [5, 6],
  8: [6, 7],
  9: [7, 8],
  10: [8, 9],
  11: [9, 10, 11],
  12: [11, 12],
  13: [12, 13, 14],
  14: [15, 16],
  15: [17, 18],
  16: [18, 19, 20],
  17: [21, 22],
  18: [23, 24, 25],
  19: [25, 26, 27],
  20: [27, 28, 29],
  21: [29, 30, 31, 32, 33],
  22: [33, 34, 35, 36],
  23: [36, 37, 38, 39],
  24: [39, 40, 41],
  25: [41, 42, 43, 44, 45],
  26: [46, 47, 48, 49, 50, 51],
  27: [51, 52, 53, 54, 55, 56, 57],
  28: [58, 59, 60, 61, 62, 63, 64, 65, 66],
  29: [67, 68, 69, 70, 71, 72, 73, 74, 75, 76, 77],
  30: [78, 79, 80, 81, 82, 83, 84, 85, 86, 87, 88, 89, 90, 91, 92, 93, 94, 95, 96, 97, 98, 99, 100, 101, 102, 103, 104, 105, 106, 107, 108, 109, 110, 111, 112, 113, 114]
};

// Direct API call
const fetchSurah = async (surahNumber) => {
  const response = await fetch(`https://api.alquran.cloud/v1/surah/${surahNumber}/ar.alafasy`);
  if (!response.ok) throw new Error('Failed to fetch');
  return response.json();
};

function QuranQuiz() {
  const { addXp } = useApp();

  const [quizSettings, setQuizSettings] = useState({
    mode: 'surah', // 'surah' or 'juz'
    surahFrom: 112,
    surahTo: 114,
    juzFrom: 30,
    juzTo: 30,
    questionCount: 10
  });

  const [quizState, setQuizState] = useState('setup'); // 'setup', 'loading', 'playing', 'results'
  const [questions, setQuestions] = useState([]);
  const [currentQuestion, setCurrentQuestion] = useState(0);
  const [answers, setAnswers] = useState([]);
  const [selectedAnswer, setSelectedAnswer] = useState(null);
  const [showResult, setShowResult] = useState(false);
  const [error, setError] = useState(null);

  const generateQuestions = async () => {
    setQuizState('loading');
    setError(null);

    try {
      let ayahPool = [];
      let surahsToFetch = [];

      if (quizSettings.mode === 'surah') {
        // Get surahs in range
        for (let i = quizSettings.surahFrom; i <= quizSettings.surahTo; i++) {
          surahsToFetch.push(i);
        }
      } else {
        // Get surahs from Juz range
        for (let j = quizSettings.juzFrom; j <= quizSettings.juzTo; j++) {
          surahsToFetch.push(...(JUZ_SURAHS[j] || []));
        }
        // Remove duplicates
        surahsToFetch = [...new Set(surahsToFetch)];
      }

      // Limit to 5 surahs for performance
      const limitedSurahs = surahsToFetch.slice(0, 5);

      // Fetch ayahs from each surah
      for (const surahNum of limitedSurahs) {
        const surah = SURAHS.find(s => s.number === surahNum);
        try {
          const res = await fetchSurah(surahNum);
          const ayahs = res.data.ayahs.map(a => ({
            surah: surahNum,
            surahName: surah?.name || `Surah ${surahNum}`,
            surahArabic: surah?.arabicName || '',
            ayahNum: a.numberInSurah,
            text: a.text
          }));
          ayahPool.push(...ayahs);
        } catch (err) {
          console.error(`Error fetching surah ${surahNum}:`, err);
        }
      }

      if (ayahPool.length < 4) {
        throw new Error('Not enough ayahs to generate quiz. Please try different surahs.');
      }

      // Shuffle and pick questions
      const shuffled = ayahPool.sort(() => Math.random() - 0.5);
      const selected = shuffled.slice(0, Math.min(quizSettings.questionCount, shuffled.length));

      // Generate questions
      const generatedQuestions = selected.map((ayah) => {
        const words = ayah.text.split(' ').filter(w => w.length > 0);
        const questionType = Math.random() > 0.5 ? 'complete' : 'identify';

        if (questionType === 'complete' && words.length > 4) {
          // Complete the ayah question
          const blankIndex = Math.floor(Math.random() * (words.length - 2)) + 1;
          const correctWord = words[blankIndex];
          const displayText = words.map((w, i) => i === blankIndex ? '_____' : w).join(' ');

          // Generate wrong options from other ayahs
          const wrongOptions = shuffled
            .filter(a => a !== ayah)
            .slice(0, 3)
            .map(a => {
              const w = a.text.split(' ').filter(word => word.length > 0);
              return w[Math.floor(Math.random() * w.length)] || 'ÙƒÙ„Ù…Ø©';
            });

          const options = [correctWord, ...wrongOptions].sort(() => Math.random() - 0.5);

          return {
            type: 'complete',
            question: `Complete the ayah from Surah ${ayah.surahName}:`,
            displayText,
            correctAnswer: correctWord,
            options,
            fullAyah: ayah.text,
            surahName: ayah.surahName
          };
        } else {
          // Identify the surah question
          const wrongSurahs = SURAHS
            .filter(s => s.name !== ayah.surahName)
            .sort(() => Math.random() - 0.5)
            .slice(0, 3)
            .map(s => s.name);

          const options = [ayah.surahName, ...wrongSurahs].sort(() => Math.random() - 0.5);

          return {
            type: 'identify',
            question: 'Which Surah is this ayah from?',
            displayText: ayah.text,
            correctAnswer: ayah.surahName,
            options,
            fullAyah: ayah.text,
            ayahNum: ayah.ayahNum,
            surahName: ayah.surahName
          };
        }
      });

      setQuestions(generatedQuestions);
      setQuizState('playing');
      setCurrentQuestion(0);
      setAnswers([]);
      setSelectedAnswer(null);
      setShowResult(false);
    } catch (err) {
      console.error('Error generating quiz:', err);
      setError(err.message || 'Failed to generate quiz. Please check your internet connection.');
      setQuizState('setup');
    }
  };

  const handleAnswer = (answer) => {
    if (showResult) return;
    setSelectedAnswer(answer);
    setShowResult(true);

    const isCorrect = answer === questions[currentQuestion].correctAnswer;
    setAnswers([...answers, { 
      questionIndex: currentQuestion, 
      answer, 
      correct: isCorrect 
    }]);
  };

  const nextQuestion = () => {
    if (currentQuestion < questions.length - 1) {
      setCurrentQuestion(currentQuestion + 1);
      setSelectedAnswer(null);
      setShowResult(false);
    } else {
      setQuizState('results');
      const correctCount = answers.filter(a => a.correct).length + 
        (selectedAnswer === questions[currentQuestion]?.correctAnswer ? 1 : 0);
      const xpEarned = Math.round((correctCount / questions.length) * 50);
      addXp(xpEarned);
    }
  };

  const getScoreMessage = (percentage) => {
    if (percentage >= 90) return { emoji: 'ðŸ†', message: 'Excellent! Mashallah!' };
    if (percentage >= 70) return { emoji: 'â­', message: 'Great job! Keep it up!' };
    if (percentage >= 50) return { emoji: 'ðŸ‘', message: 'Good effort! Practice more!' };
    return { emoji: 'ðŸ“š', message: 'Keep practicing! You\'ll improve!' };
  };

  // Loading State
  if (quizState === 'loading') {
    return (
      <div className="quiz-loading">
        <div className="spinner" />
        <p>Generating quiz questions...</p>
        <span>Fetching ayahs from the Quran</span>
        <style>{`
          .quiz-loading {
            display: flex;
            flex-direction: column;
            align-items: center;
            justify-content: center;
            padding: 80px 20px;
            text-align: center;
          }
          .spinner {
            width: 48px;
            height: 48px;
            border: 4px solid var(--bg-surface-light);
            border-top-color: var(--gold);
            border-radius: 50%;
            animation: spin 1s linear infinite;
            margin-bottom: 20px;
          }
          @keyframes spin {
            to { transform: rotate(360deg); }
          }
          .quiz-loading p {
            font-size: 16px;
            margin-bottom: 8px;
          }
          .quiz-loading span {
            font-size: 13px;
            color: var(--text-muted);
          }
        `}</style>
      </div>
    );
  }

  // Setup View
  if (quizState === 'setup') {
    return (
      <div className="quiz-setup">
        <div className="setup-header">
          <HelpCircle size={32} className="icon" />
          <h2>Quran Quiz</h2>
          <p>Test your memorization knowledge</p>
        </div>

        {error && (
          <div className="error-banner">
            <X size={16} />
            <span>{error}</span>
          </div>
        )}

        <div className="setting-group">
          <label>Quiz Mode</label>
          <div className="mode-selector">
            <button 
              className={`mode-btn ${quizSettings.mode === 'surah' ? 'active' : ''}`}
              onClick={() => setQuizSettings(prev => ({ ...prev, mode: 'surah' }))}
            >
              By Surah
            </button>
            <button 
              className={`mode-btn ${quizSettings.mode === 'juz' ? 'active' : ''}`}
              onClick={() => setQuizSettings(prev => ({ ...prev, mode: 'juz' }))}
            >
              By Juz
            </button>
          </div>
        </div>

        {quizSettings.mode === 'surah' ? (
          <div className="setting-group">
            <label>Surah Range</label>
            <div className="range-row">
              <div className="range-select">
                <span className="range-label">From</span>
                <select 
                  value={quizSettings.surahFrom}
                  onChange={(e) => {
                    const val = parseInt(e.target.value);
                    setQuizSettings(prev => ({ 
                      ...prev, 
                      surahFrom: val,
                      surahTo: Math.max(val, prev.surahTo)
                    }));
                  }}
                >
                  {SURAHS.map(s => (
                    <option key={s.number} value={s.number}>
                      {s.number}. {s.name}
                    </option>
                  ))}
                </select>
              </div>
              <div className="range-select">
                <span className="range-label">To</span>
                <select 
                  value={quizSettings.surahTo}
                  onChange={(e) => setQuizSettings(prev => ({ 
                    ...prev, 
                    surahTo: parseInt(e.target.value)
                  }))}
                >
                  {SURAHS.filter(s => s.number >= quizSettings.surahFrom).map(s => (
                    <option key={s.number} value={s.number}>
                      {s.number}. {s.name}
                    </option>
                  ))}
                </select>
              </div>
            </div>
            <span className="helper-text">
              {quizSettings.surahTo - quizSettings.surahFrom + 1} surah(s) selected
            </span>
          </div>
        ) : (
          <div className="setting-group">
            <label>Juz Range</label>
            <div className="range-row">
              <div className="range-select">
                <span className="range-label">From Juz</span>
                <select 
                  value={quizSettings.juzFrom}
                  onChange={(e) => {
                    const val = parseInt(e.target.value);
                    setQuizSettings(prev => ({ 
                      ...prev, 
                      juzFrom: val,
                      juzTo: Math.max(val, prev.juzTo)
                    }));
                  }}
                >
                  {Array.from({ length: 30 }, (_, i) => i + 1).map(j => (
                    <option key={j} value={j}>Juz {j}</option>
                  ))}
                </select>
              </div>
              <div className="range-select">
                <span className="range-label">To Juz</span>
                <select 
                  value={quizSettings.juzTo}
                  onChange={(e) => setQuizSettings(prev => ({ 
                    ...prev, 
                    juzTo: parseInt(e.target.value)
                  }))}
                >
                  {Array.from({ length: 30 }, (_, i) => i + 1)
                    .filter(j => j >= quizSettings.juzFrom)
                    .map(j => (
                      <option key={j} value={j}>Juz {j}</option>
                    ))}
                </select>
              </div>
            </div>
            <span className="helper-text">
              {quizSettings.juzTo - quizSettings.juzFrom + 1} juz selected
            </span>
          </div>
        )}

        <div className="setting-group">
          <label>Number of Questions</label>
          <div className="question-count-selector">
            {[5, 10, 15, 20].map(num => (
              <button
                key={num}
                className={`count-btn ${quizSettings.questionCount === num ? 'active' : ''}`}
                onClick={() => setQuizSettings(prev => ({ ...prev, questionCount: num }))}
              >
                {num}
              </button>
            ))}
          </div>
        </div>

        <button 
          className="btn btn-primary start-quiz-btn"
          onClick={generateQuestions}
        >
          <Play size={18} /> Start Quiz
        </button>

        <style>{`
          .quiz-setup {
            animation: fadeIn 0.3s ease-out;
          }
          .setup-header {
            text-align: center;
            padding: 30px 20px;
            background: var(--bg-surface);
            border-radius: 16px;
            margin-bottom: 20px;
          }
          .setup-header .icon {
            color: var(--gold);
            margin-bottom: 12px;
          }
          .setup-header h2 {
            font-size: 22px;
            margin-bottom: 8px;
          }
          .setup-header p {
            color: var(--text-muted);
            font-size: 13px;
          }
          .error-banner {
            display: flex;
            align-items: center;
            gap: 8px;
            background: rgba(231,76,60,0.2);
            color: var(--error);
            padding: 12px 16px;
            border-radius: 10px;
            margin-bottom: 16px;
            font-size: 13px;
          }
          .setting-group {
            background: var(--bg-surface);
            padding: 16px;
            border-radius: 12px;
            margin-bottom: 12px;
          }
          .setting-group label {
            display: block;
            font-size: 13px;
            font-weight: 600;
            margin-bottom: 12px;
          }
          .mode-selector {
            display: grid;
            grid-template-columns: 1fr 1fr;
            gap: 8px;
          }
          .mode-btn {
            padding: 12px;
            background: var(--bg-surface-light);
            border: 2px solid transparent;
            border-radius: 8px;
            color: var(--text-secondary);
            font-size: 13px;
            font-weight: 600;
            cursor: pointer;
          }
          .mode-btn.active {
            background: var(--primary);
            border-color: var(--gold);
            color: var(--gold);
          }
          .range-row {
            display: grid;
            grid-template-columns: 1fr 1fr;
            gap: 12px;
          }
          .range-select {
            display: flex;
            flex-direction: column;
            gap: 6px;
          }
          .range-label {
            font-size: 11px;
            color: var(--text-muted);
            text-transform: uppercase;
          }
          select {
            width: 100%;
            padding: 12px;
            background: var(--bg-surface-light);
            border: none;
            border-radius: 8px;
            color: var(--text-primary);
            font-size: 13px;
          }
          .helper-text {
            display: block;
            font-size: 12px;
            color: var(--gold);
            margin-top: 10px;
          }
          .question-count-selector {
            display: grid;
            grid-template-columns: repeat(4, 1fr);
            gap: 8px;
          }
          .count-btn {
            padding: 12px;
            background: var(--bg-surface-light);
            border: 2px solid transparent;
            border-radius: 8px;
            color: var(--text-secondary);
            font-size: 14px;
            font-weight: 600;
            cursor: pointer;
          }
          .count-btn.active {
            background: var(--primary);
            border-color: var(--gold);
            color: var(--gold);
          }
          .start-quiz-btn {
            width: 100%;
            padding: 16px;
            font-size: 16px;
            margin-top: 8px;
          }
        `}</style>
      </div>
    );
  }

  // Playing View
  if (quizState === 'playing' && questions.length > 0) {
    const question = questions[currentQuestion];

    return (
      <div className="quiz-playing">
        {/* Progress */}
        <div className="quiz-progress">
          <span>Question {currentQuestion + 1} of {questions.length}</span>
          <div className="progress-bar">
            <div className="progress-fill" style={{ width: `${((currentQuestion + 1) / questions.length) * 100}%` }} />
          </div>
        </div>

        {/* Question */}
        <div className="question-card">
          <span className="question-type">{question.type === 'complete' ? 'ðŸ“ Complete the Ayah' : 'ðŸ” Identify the Surah'}</span>
          <p className="question-text">{question.question}</p>
          <p className="arabic display-ayah">{question.displayText}</p>
        </div>

        {/* Options */}
        <div className="options-grid">
          {question.options.map((option, i) => {
            const isSelected = selectedAnswer === option;
            const isCorrect = option === question.correctAnswer;
            let className = 'option-btn';

            if (showResult) {
              if (isCorrect) className += ' correct';
              else if (isSelected && !isCorrect) className += ' wrong';
            } else if (isSelected) {
              className += ' selected';
            }

            return (
              <button
                key={i}
                className={className}
                onClick={() => handleAnswer(option)}
                disabled={showResult}
              >
                {question.type === 'complete' ? (
                  <span className="arabic option-arabic">{option}</span>
                ) : (
                  <span>{option}</span>
                )}
                {showResult && isCorrect && <Check size={18} className="result-icon" />}
                {showResult && isSelected && !isCorrect && <X size={18} className="result-icon" />}
              </button>
            );
          })}
        </div>

        {/* Result feedback */}
        {showResult && (
          <div className={`feedback ${selectedAnswer === question.correctAnswer ? 'correct' : 'wrong'}`}>
            {selectedAnswer === question.correctAnswer ? (
              <p>âœ… Correct! Mashallah!</p>
            ) : (
              <p>âŒ The correct answer is highlighted above.</p>
            )}
          </div>
        )}

        {/* Next button */}
        {showResult && (
          <button className="btn btn-primary next-btn" onClick={nextQuestion}>
            {currentQuestion < questions.length - 1 ? 'Next Question' : 'See Results'}
          </button>
        )}

        <style>{`
          .quiz-playing {
            animation: fadeIn 0.3s ease-out;
          }
          .quiz-progress {
            margin-bottom: 20px;
          }
          .quiz-progress span {
            font-size: 12px;
            color: var(--text-muted);
          }
          .progress-bar {
            height: 6px;
            background: var(--bg-surface);
            border-radius: 3px;
            margin-top: 8px;
            overflow: hidden;
          }
          .progress-fill {
            height: 100%;
            background: var(--gold);
            transition: width 0.3s ease;
          }
          .question-card {
            background: var(--bg-surface);
            padding: 24px;
            border-radius: 16px;
            margin-bottom: 20px;
            text-align: center;
          }
          .question-type {
            font-size: 12px;
            color: var(--gold);
            margin-bottom: 12px;
            display: block;
          }
          .question-text {
            font-size: 14px;
            color: var(--text-secondary);
            margin-bottom: 16px;
          }
          .display-ayah {
            font-size: 24px;
            line-height: 2;
            color: var(--text-primary);
          }
          .options-grid {
            display: grid;
            grid-template-columns: 1fr 1fr;
            gap: 10px;
            margin-bottom: 16px;
          }
          .option-btn {
            display: flex;
            align-items: center;
            justify-content: center;
            gap: 8px;
            padding: 16px;
            background: var(--bg-surface);
            border: 2px solid transparent;
            border-radius: 12px;
            color: var(--text-primary);
            font-size: 14px;
            cursor: pointer;
            transition: all 0.2s ease;
          }
          .option-btn:hover:not(:disabled) {
            border-color: var(--primary);
          }
          .option-btn.selected {
            border-color: var(--gold);
            background: rgba(212,175,55,0.1);
          }
          .option-btn.correct {
            border-color: var(--success);
            background: rgba(46,204,113,0.2);
          }
          .option-btn.wrong {
            border-color: var(--error);
            background: rgba(231,76,60,0.2);
          }
          .option-arabic {
            font-size: 18px;
          }
          .result-icon {
            flex-shrink: 0;
          }
          .feedback {
            padding: 12px;
            border-radius: 10px;
            text-align: center;
            margin-bottom: 16px;
          }
          .feedback.correct {
            background: rgba(46,204,113,0.2);
            color: var(--success);
          }
          .feedback.wrong {
            background: rgba(231,76,60,0.2);
            color: var(--error);
          }
          .next-btn {
            width: 100%;
          }
        `}</style>
      </div>
    );
  }

  // Results View
  if (quizState === 'results') {
    const correctCount = answers.filter(a => a.correct).length;
    const percentage = Math.round((correctCount / questions.length) * 100);
    const { emoji, message } = getScoreMessage(percentage);
    const xpEarned = Math.round((correctCount / questions.length) * 50);

    return (
      <div className="quiz-results">
        <div className="results-card">
          <span className="result-emoji">{emoji}</span>
          <h2>Quiz Complete!</h2>
          <p className="result-message">{message}</p>

          <div className="score-circle">
            <span className="score-value">{correctCount}/{questions.length}</span>
            <span className="score-percent">{percentage}%</span>
          </div>

          <div className="stats-row">
            <div className="stat">
              <Check size={16} className="correct" />
              <span>{correctCount} Correct</span>
            </div>
            <div className="stat">
              <X size={16} className="wrong" />
              <span>{questions.length - correctCount} Wrong</span>
            </div>
          </div>

          <div className="xp-earned">
            <Award size={20} />
            <span>+{xpEarned} XP earned</span>
          </div>
        </div>

        {/* Question breakdown */}
        <div className="breakdown">
          <h3>Question Breakdown</h3>
          <div className="breakdown-list">
            {answers.map((answer, i) => (
              <div key={i} className={`breakdown-item ${answer.correct ? 'correct' : 'wrong'}`}>
                <span className="q-num">Q{i + 1}</span>
                {answer.correct ? <Check size={16} /> : <X size={16} />}
              </div>
            ))}
          </div>
        </div>

        <div className="result-actions">
          <button className="btn btn-primary" onClick={() => setQuizState('setup')}>
            <RotateCcw size={18} /> New Quiz
          </button>
        </div>

        <style>{`
          .quiz-results {
            animation: fadeIn 0.3s ease-out;
          }
          .results-card {
            background: var(--bg-surface);
            border-radius: 20px;
            padding: 32px;
            text-align: center;
            margin-bottom: 20px;
          }
          .result-emoji {
            font-size: 48px;
            display: block;
            margin-bottom: 16px;
          }
          .results-card h2 {
            font-size: 24px;
            margin-bottom: 8px;
          }
          .result-message {
            color: var(--text-secondary);
            margin-bottom: 24px;
          }
          .score-circle {
            width: 120px;
            height: 120px;
            border-radius: 50%;
            background: linear-gradient(135deg, var(--primary) 0%, var(--primary-light) 100%);
            display: flex;
            flex-direction: column;
            align-items: center;
            justify-content: center;
            margin: 0 auto 24px;
          }
          .score-value {
            font-size: 28px;
            font-weight: 700;
            font-family: 'Space Grotesk', sans-serif;
          }
          .score-percent {
            font-size: 14px;
            color: var(--gold);
          }
          .stats-row {
            display: flex;
            justify-content: center;
            gap: 24px;
            margin-bottom: 20px;
          }
          .stat {
            display: flex;
            align-items: center;
            gap: 6px;
            font-size: 14px;
          }
          .stat .correct { color: var(--success); }
          .stat .wrong { color: var(--error); }
          .xp-earned {
            display: inline-flex;
            align-items: center;
            gap: 8px;
            background: rgba(212,175,55,0.2);
            padding: 10px 20px;
            border-radius: 20px;
            color: var(--gold);
            font-weight: 600;
          }
          .breakdown {
            background: var(--bg-surface);
            border-radius: 16px;
            padding: 20px;
            margin-bottom: 20px;
          }
          .breakdown h3 {
            font-size: 14px;
            color: var(--text-secondary);
            margin-bottom: 16px;
          }
          .breakdown-list {
            display: flex;
            flex-wrap: wrap;
            gap: 8px;
          }
          .breakdown-item {
            display: flex;
            align-items: center;
            gap: 4px;
            padding: 8px 12px;
            border-radius: 8px;
            font-size: 12px;
          }
          .breakdown-item.correct {
            background: rgba(46,204,113,0.2);
            color: var(--success);
          }
          .breakdown-item.wrong {
            background: rgba(231,76,60,0.2);
            color: var(--error);
          }
          .q-num {
            font-weight: 600;
          }
          .result-actions .btn {
            width: 100%;
          }
        `}</style>
      </div>
    );
  }

  return null;
}

export default QuranQuiz;
