import React, { createContext, useContext, useState, useEffect } from 'react';

const AppContext = createContext();

export function AppProvider({ children }) {
  const [user, setUser] = useState(null);
  const [prayerTimes, setPrayerTimes] = useState(null);
  const [location, setLocation] = useState(null);
  const [xp, setXp] = useState(() => {
    const saved = localStorage.getItem('qawaam_xp');
    return saved ? parseInt(saved) : 0;
  });
  const [streak, setStreak] = useState(() => {
    const saved = localStorage.getItem('qawaam_streak');
    return saved ? parseInt(saved) : 0;
  });
  const [habits, setHabits] = useState(() => {
    const saved = localStorage.getItem('qawaam_habits');
    return saved ? JSON.parse(saved) : [
      { id: '1', name: 'Wake up for Fajr', completed: false },
      { id: '2', name: 'Read Quran', completed: false },
      { id: '3', name: 'Morning Adhkar', completed: false },
      { id: '4', name: 'Evening Adhkar', completed: false },
      { id: '5', name: 'Drink 8 glasses of water', completed: false },
    ];
  });
  const [prayers, setPrayers] = useState(() => {
    const saved = localStorage.getItem('qawaam_prayers');
    const today = new Date().toDateString();
    if (saved) {
      const parsed = JSON.parse(saved);
      if (parsed.date === today) return parsed.prayers;
    }
    return {
      fajr: null, dhuhr: null, asr: null, maghrib: null, isha: null
    };
  });

  // Save to localStorage
  useEffect(() => {
    localStorage.setItem('qawaam_xp', xp.toString());
  }, [xp]);

  useEffect(() => {
    localStorage.setItem('qawaam_streak', streak.toString());
  }, [streak]);

  useEffect(() => {
    localStorage.setItem('qawaam_habits', JSON.stringify(habits));
  }, [habits]);

  useEffect(() => {
    localStorage.setItem('qawaam_prayers', JSON.stringify({
      date: new Date().toDateString(),
      prayers
    }));
  }, [prayers]);

  // Get location on mount
  useEffect(() => {
    if (navigator.geolocation) {
      navigator.geolocation.getCurrentPosition(
        (pos) => {
          setLocation({
            latitude: pos.coords.latitude,
            longitude: pos.coords.longitude
          });
        },
        (err) => {
          // Default to Mecca if location denied
          setLocation({ latitude: 21.4225, longitude: 39.8262 });
        }
      );
    }
  }, []);

  const addXp = (amount) => {
    setXp(prev => prev + amount);
  };

  const markPrayer = (prayer, status) => {
    setPrayers(prev => ({ ...prev, [prayer]: status }));
    if (status === 'on_time') addXp(50);
    else if (status === 'late') addXp(20);
  };

  const toggleHabit = (habitId) => {
    setHabits(prev => prev.map(h => {
      if (h.id === habitId) {
        if (!h.completed) addXp(10);
        return { ...h, completed: !h.completed };
      }
      return h;
    }));
  };

  const getLevel = () => {
    if (xp < 500) return { name: 'Beginner', level: 1, nextXp: 500 };
    if (xp < 1500) return { name: 'Steadfast', level: 2, nextXp: 1500 };
    if (xp < 3500) return { name: 'Consistent', level: 3, nextXp: 3500 };
    if (xp < 7500) return { name: 'Firm', level: 4, nextXp: 7500 };
    return { name: 'Master', level: 5, nextXp: null };
  };

  return (
    <AppContext.Provider value={{
      user, setUser,
      prayerTimes, setPrayerTimes,
      location, setLocation,
      xp, addXp,
      streak, setStreak,
      habits, setHabits, toggleHabit,
      prayers, markPrayer,
      getLevel,
    }}>
      {children}
    </AppContext.Provider>
  );
}

export const useApp = () => useContext(AppContext);
