import React, { useState, useEffect } from 'react';
import { useApp } from '../context/AppContext';
import { BookOpen, Play, Check, ChevronRight, Volume2 } from 'lucide-react';
import axios from 'axios';

const MEMORIZATION_SURAHS = [
  { number: 1, name: 'Al-Fatihah', arabicName: 'ÿßŸÑŸÅÿßÿ™ÿ≠ÿ©', ayahs: 7 },
  { number: 112, name: 'Al-Ikhlas', arabicName: 'ÿßŸÑÿ•ÿÆŸÑÿßÿµ', ayahs: 4 },
  { number: 113, name: 'Al-Falaq', arabicName: 'ÿßŸÑŸÅŸÑŸÇ', ayahs: 5 },
  { number: 114, name: 'An-Nas', arabicName: 'ÿßŸÑŸÜÿßÿ≥', ayahs: 6 },
  { number: 111, name: 'Al-Masad', arabicName: 'ÿßŸÑŸÖÿ≥ÿØ', ayahs: 5 },
  { number: 110, name: 'An-Nasr', arabicName: 'ÿßŸÑŸÜÿµÿ±', ayahs: 3 },
  { number: 109, name: 'Al-Kafirun', arabicName: 'ÿßŸÑŸÉÿßŸÅÿ±ŸàŸÜ', ayahs: 6 },
  { number: 108, name: 'Al-Kawthar', arabicName: 'ÿßŸÑŸÉŸàÿ´ÿ±', ayahs: 3 },
];

function Quran() {
  const { addXp } = useApp();
  const [selectedSurah, setSelectedSurah] = useState(null);
  const [surahData, setSurahData] = useState(null);
  const [loading, setLoading] = useState(false);
  const [memorized, setMemorized] = useState(() => {
    const saved = localStorage.getItem('qawaam_memorized');
    return saved ? JSON.parse(saved) : [];
  });
  const [tab, setTab] = useState('memorize');

  useEffect(() => {
    localStorage.setItem('qawaam_memorized', JSON.stringify(memorized));
  }, [memorized]);

  const loadSurah = async (surahNumber) => {
    setLoading(true);
    setSelectedSurah(surahNumber);

    try {
      const [arabicRes, translationRes] = await Promise.all([
        axios.get(`http://localhost:5000/api/surah/${surahNumber}`),
        axios.get(`http://localhost:5000/api/surah/${surahNumber}/translation`)
      ]);

      setSurahData({
        arabic: arabicRes.data.data,
        translation: translationRes.data.data
      });
    } catch (error) {
      console.error('Error loading surah:', error);
    }

    setLoading(false);
  };

  const markAsMemorized = (surahNumber) => {
    if (!memorized.includes(surahNumber)) {
      setMemorized([...memorized, surahNumber]);
      addXp(100);
    }
  };

  const closeSurah = () => {
    setSelectedSurah(null);
    setSurahData(null);
  };

  return (
    <div className="quran-page">
      <header className="page-header">
        <h1>üìñ Quran</h1>
        <p className="subtitle">Memorize & Learn</p>
      </header>

      <div className="tabs">
        <button 
          className={`tab ${tab === 'memorize' ? 'active' : ''}`}
          onClick={() => setTab('memorize')}
        >
          Memorize
        </button>
        <button 
          className={`tab ${tab === 'review' ? 'active' : ''}`}
          onClick={() => setTab('review')}
        >
          Review ({memorized.length})
        </button>
      </div>

      {!selectedSurah ? (
        <div className="surah-grid">
          {MEMORIZATION_SURAHS.map((surah) => {
            const isMemorized = memorized.includes(surah.number);

            return (
              <div 
                key={surah.number}
                className={`surah-card ${isMemorized ? 'memorized' : ''}`}
                onClick={() => loadSurah(surah.number)}
              >
                <div className="surah-number">{surah.number}</div>
                <div className="surah-info">
                  <h3>{surah.name}</h3>
                  <span className="arabic-name">{surah.arabicName}</span>
                  <span className="ayah-count">{surah.ayahs} ayahs</span>
                </div>
                {isMemorized && (
                  <div className="memorized-badge">
                    <Check size={16} />
                  </div>
                )}
                <ChevronRight className="chevron" size={20} />
              </div>
            );
          })}
        </div>
      ) : (
        <div className="surah-view">
          <button className="back-btn" onClick={closeSurah}>
            ‚Üê Back to Surahs
          </button>

          {loading ? (
            <div className="loading">Loading...</div>
          ) : surahData ? (
            <>
              <div className="surah-header">
                <h2 className="arabic">{surahData.arabic.name}</h2>
                <p>{surahData.arabic.englishName} - {surahData.arabic.englishNameTranslation}</p>
              </div>

              <div className="ayahs-list">
                {surahData.arabic.ayahs.map((ayah, index) => (
                  <div key={ayah.number} className="ayah-item">
                    <div className="ayah-number">{ayah.numberInSurah}</div>
                    <div className="ayah-content">
                      <p className="arabic ayah-arabic">{ayah.text}</p>
                      <p className="ayah-translation">
                        {surahData.translation.ayahs[index]?.text}
                      </p>
                    </div>
                  </div>
                ))}
              </div>

              {!memorized.includes(selectedSurah) && (
                <button 
                  className="btn btn-primary mark-memorized"
                  onClick={() => markAsMemorized(selectedSurah)}
                >
                  <Check size={18} />
                  Mark as Memorized (+100 XP)
                </button>
              )}
            </>
          ) : null}
        </div>
      )}

      <style>{`
        .quran-page {
          padding: 20px;
          padding-bottom: 100px;
        }
        .page-header {
          margin-bottom: 20px;
        }
        .page-header h1 {
          font-size: 28px;
          margin-bottom: 4px;
        }
        .subtitle {
          color: var(--text-muted);
          font-size: 14px;
        }
        .tabs {
          display: flex;
          gap: 8px;
          margin-bottom: 20px;
        }
        .tab {
          flex: 1;
          padding: 12px;
          border: none;
          background: var(--bg-surface);
          color: var(--text-secondary);
          border-radius: 12px;
          font-weight: 600;
          cursor: pointer;
          transition: all 0.2s ease;
        }
        .tab.active {
          background: var(--primary);
          color: var(--text-primary);
        }
        .surah-grid {
          display: flex;
          flex-direction: column;
          gap: 12px;
        }
        .surah-card {
          display: flex;
          align-items: center;
          gap: 12px;
          background: var(--bg-surface);
          padding: 16px;
          border-radius: 16px;
          cursor: pointer;
          transition: all 0.2s ease;
        }
        .surah-card:hover {
          background: var(--bg-surface-light);
          transform: translateX(4px);
        }
        .surah-card.memorized {
          border-left: 4px solid var(--success);
        }
        .surah-number {
          width: 40px;
          height: 40px;
          background: var(--primary);
          border-radius: 10px;
          display: flex;
          align-items: center;
          justify-content: center;
          font-weight: 700;
          font-size: 14px;
        }
        .surah-info {
          flex: 1;
        }
        .surah-info h3 {
          font-size: 16px;
          margin-bottom: 4px;
        }
        .arabic-name {
          font-family: 'Amiri', serif;
          color: var(--gold);
          font-size: 16px;
          margin-right: 8px;
        }
        .ayah-count {
          color: var(--text-muted);
          font-size: 12px;
        }
        .memorized-badge {
          background: var(--success);
          color: white;
          width: 28px;
          height: 28px;
          border-radius: 50%;
          display: flex;
          align-items: center;
          justify-content: center;
        }
        .chevron {
